                        ((saldogruppe erLik "c") og ((gevinstOverfoertTilGevinstOgTapskonto.beloep er oppgitt) eller (tapOverfoertTilGevinstOgTapskonto.beloep er oppgitt))) eller
                        ((saldogruppe erLik "c2") og ((gevinstOverfoertTilGevinstOgTapskonto.beloep er oppgitt) eller (tapOverfoertTilGevinstOgTapskonto.beloep er oppgitt))) eller
                        ((saldogruppe erLik "d") og ((gevinstOverfoertTilGevinstOgTapskonto.beloep er oppgitt) eller (tapOverfoertTilGevinstOgTapskonto.beloep er oppgitt))) eller
                        ((saldogruppe erLik "j") og ((gevinstOverfoertTilGevinstOgTapskonto.beloep er oppgitt) eller (tapOverfoertTilGevinstOgTapskonto.beloep er oppgitt)))
                }) {
                    betjeningsstrategiSettesTil(
                        ikke, faktiskFeil, 1
                    )
                }
            }
    }

sakstype(Personlig)
         .kontekst(ValideringSkattepliktig) {

