sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {

        // hvis valgt skjermingsgrunnlagtype er foretaksgjeld eller leverandørgjeld og
        //et eller flere beløp i inngående eller utgående verdi av foretaksgjeld eller leverandørgjeld er oppgitt med minusfortegn
        naeringsspesifikasjon
            .forForekomsterAv{fordeltBeregnetPersoninntekt}
            .forForekomsterAv{spesifikasjonAvSkjermingsgrunnlag}
            .utslagFor {

                val inngåendeVerdiForetaksgjeld =
                    filtrer { fastsatt(skjermingsgrunnlagstype) erLik "foretaksgjeld" }
                        .summer { inngaaendeVerdi.beloep }

                val utgåendeVerdiForetaksgjeld =
                    filtrer { fastsatt(skjermingsgrunnlagstype) erLik "foretaksgjeld" }
                        .summer { utgaaendeVerdi.beloep }

                val inngåendeVerdileverandoergjeld =
                    filtrer { fastsatt(skjermingsgrunnlagstype) erLik "leverandoergjeld" }
                        .summer { inngaaendeVerdi.beloep }

                val utgåendeVerdiLeverandoergjeld =
                    filtrer { fastsatt(skjermingsgrunnlagstype) erLik "leverandoergjeld" }
                        .summer { utgaaendeVerdi.beloep }

          hvis((inngåendeVerdiForetaksgjeld erMindreEnn 0) eller
              (utgåendeVerdiForetaksgjeld erMindreEnn 0) eller
              (inngåendeVerdileverandoergjeld erMindreEnn 0) eller
              (utgåendeVerdiLeverandoergjeld erMindreEnn 0))
              {
                    betjeningsstrategiSettesTil(
                        ikke, merknadStandard
                    )
                }
            }
    }

