sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {

        //kontrollen gir utslag når sjablongberegnet fradrag i skogbruk i fordelt beregnet næringsinntekt er ulik sjablongberegnet inntekt i jordbruk i fordelt beregnet næringsinntekt
        //kontrollen gir merknad i valideringstjenesten

        naeringsspesifikasjon
      		.utslagFor {

              val sjablongFradragBiomasseVed = forForekomsterAv{fordeltBeregnetNaeringsinntektForPersonligSkattepliktigEllerSdf}
                 .summer{sjablongberegnetFradragFraBiomasseVedproduksjon.beloep}

              val sjablongInntektBiomasseVed = forForekomsterAv{fordeltBeregnetNaeringsinntektForPersonligSkattepliktigEllerSdf}
                 .summer{sjablongberegnetInntektFraBiomasseVedproduksjon.beloep}

              val diffSjablongBiomasseVed = differanse(sjablongFradragBiomasseVed, sjablongInntektBiomasseVed)

              hvis(diffSjablongBiomasseVed erStørreEnn 1_000)

                {
                    betjeningsstrategiSettesTil(
                        ikke,
                        faktiskFeil,
                      Avviksbeloep settesTil diffSjablongBiomasseVed
                    )
                }
            }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {

        //kontrollen gir utslag når sjablongberegnet fradrag i skogbruk i fordelt beregnet næringsinntekt er ulik sjablongberegnet inntekt i jordbruk i fordelt beregnet næringsinntekt
        //kontrollen gir merknad i valideringstjenesten

        naeringsspesifikasjon
            .forForekomsterAv{fordeltBeregnetNaeringsinntektForPersonligSkattepliktigEllerSdf}
            .utslagFor {

                val sjablongISkogbruk = hvorMinstEnForekomst { ((naeringstype erLik "skogbruk") og (sjablongberegnetInntektFraBiomasseVedproduksjon.beloep er oppgitt)) }

                val sjablongIJordbruk = hvorMinstEnForekomst { ((naeringstype erLik "jordbrukGartneriPelsdyrnaeringMv") og (sjablongberegnetInntektFraBiomasseVedproduksjon.beloep er oppgitt)) }

                val sumSjablongISkogOgJord = summer { sjablongberegnetInntektFraBiomasseVedproduksjon.beloep }

                hvis(sjablongISkogbruk og sjablongIJordbruk og (sumSjablongISkogOgJord erUlik 0))

                {
                    betjeningsstrategiSettesTil(
                        ikke,
                        merknadStandard,
                        Avviksbeloep settesTil sumSjablongISkogOgJord
                    )
                }
            }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        // kontrollen gir utslag når sjablongberegnet fradrag i skogbruk pr tømmerkonto er høyere enn 55 % av salgsinntekt/uttak ved og biomasse
        // kontrollen gir merknad i valideringstjenesten

        naeringsspesifikasjon
            .forForekomsterAv { skogbruk.skogOgToemmerkonto }
            .utslagFor {
                val maxSjablongberegnetFradrag =
                    verdiFor { inntektOgKostnad.salgsinntektOgUttakAvVedOgBiomasse.beloep * 55.prosent }

                hvis(verdiFor { inntektOgKostnad.sjablongberegnetFradragKnyttetTilBiomasseOgVedproduksjon.beloep } erStørreEnn maxSjablongberegnetFradrag) {
                    betjeningsstrategiSettesTil(
                        ikke,
                        merknadStandard,
                        1
                    )
                }
            }
    }

sakstype(Upersonlig, SDF)
    .kontekst(ValideringFoerBeregning) {
        // kontrollen gir utslag dersom skattepliktig med virksomhetstype ulik enkeltpersonforetak rapporterer skogbruk
        // kontrollen gir faktiskFeil i valideringstjenesten
        val harVirksomhetstypeUlikEnkeltpersonforetak = naeringsspesifikasjon
            .forForekomsterAv { virksomhet }
            .hvorMinstEnForekomst {
                virksomhetstype.finnesBlant("oevrigSelskap", "selskapMedDeltakerfastsetting", "bankOgFinansieringsforetak",
                                            "livsforsikringsforetakOgPensjonskasse", "skadeforsikringsforetak", "samvirkeforetak", "nokus")
            }

        val rapportererSkogbruk = naeringsspesifikasjon
            .forForekomsterAv { skogbruk.skogOgToemmerkonto }
            .hvorMinstEnForekomst { driftsresultatSomKanOverfoeresTilToemmerkonto.beloep er oppgitt }

        naeringsspesifikasjon
            .utslagFor {

                hvis(harVirksomhetstypeUlikEnkeltpersonforetak og rapportererSkogbruk) {
                    betjeningsstrategiSettesTil(
                        ikke,
                        faktiskFeil,
                        1,
                        Gruppering settesTil Næring
                    )
                }
            }
    }

sakstype(Personlig)
    	.kontekst(ValideringSkattepliktig){
    // kontrollen gir utslag når beløpet fylt ut i feltet 'Sykepenger fisker' avviker fra innrapportert beløp. Gjelder både når beløpet er større eller mindre enn innrapportert beløp.

    utgåtteSkattemeldingFelter {
        skattemeldingUtgått
        .forForekomsterAv(sti(oppgavegiver, sykepengerOgAnnenInntektMvFraOppgavegiver))
        .utslagFor {
            val harSykepengerFisker = filtrer { behandlingsart erLik "SYKEPENGER_FISKER" }
                .hvorMinstEnForekomst { grunnlag(beloepSomGruppe.beloepSomTall) er oppgitt }

            val fastsattSykepengerFisker = filtrer { behandlingsart erLik "SYKEPENGER_FISKER" }
                .summer { fastsatt(beloepSomGruppe.beloepSomTall) }

            val grunnlagSykepengerFisker = filtrer { behandlingsart erLik "SYKEPENGER_FISKER" }
                .summer { grunnlag(beloepSomGruppe.beloepSomTall) }

            hvis(harSykepengerFisker og ((fastsattSykepengerFisker - grunnlagSykepengerFisker) erUlik 0))
            {
                betjeningsstrategiSettesTil(
                    ikke,
                    merknadStandard,
                    Avviksbeloep settesTil (fastsattSykepengerFisker - grunnlagSykepengerFisker)
                )
            }
        }
    }
        }

