sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
            // kontrollen gir utslag dersom skattyter i post 7830 eller 7860 har oppgitt mer enn 20 000 i kostander
    	    // AB-test på skattytere som har tallet 0, 2, 4, 6, 8 i siste siffer i partsnummeret
            // kontrollen gir merknad i valideringstjenesten og SME
    val partsnr = skattepliktGjeldende
