sakstype(Upersonlig, SDF)
    .kontekst(ValideringSkattepliktig) {

        val harSkattepliktForKraftverk =  skattepliktUpersonligGjeldende
          .forForekomsterAv{skatteplikt}
           .hvorMinstEnForekomst{erOmfattetAvSaerreglerForKraftforetak erLik true}

        val harOpplysningspliktForKraftverk =  opplysningspliktSdfGjeldende
           .forForekomsterAv{opplysningsplikt}
           .hvorMinstEnForekomst{erOmfattetAvSaerreglerForKraftforetak erLik true}

        // Kontroll som gir utslag på alle UP/SDF som er kraftverk i skatteplikt/opplysningsplikt
        // som mangler eierandel på vindkraftverk i næringsspesifikasjonen
        // eller eierandel er oppgitt som et desimaltall mellom 0 og 1
        naeringsspesifikasjon
              .forForekomsterAv{spesifikasjonAvEnhetIVindkraftverk}
            .utslagFor {

                val harKraftverkUtenEierandelEllerMedEierandelUnder1prosent = hvorMinstEnForekomst{
                  ((kraftverketsNavn er oppgitt) eller (loepenummer er oppgitt)) og
                  ((eierandel erLik 0) eller
                  (eierandel erStørreEnn 0) og (eierandel erMindreEnn 1))
                }

                hvis((harSkattepliktForKraftverk eller harOpplysningspliktForKraftverk) og
                     harKraftverkUtenEierandelEllerMedEierandelUnder1prosent)
              {
                    betjeningsstrategiSettesTil(
                        ikke,
                        merknadStandard   )
                }
            }
    }

sakstype(Upersonlig, SDF)
    .kontekst(ValideringFoerBeregning) {

        val harSkattepliktForKraftverk =  skattepliktUpersonligGjeldende
          .forForekomsterAv{skatteplikt}
           .hvorMinstEnForekomst{erOmfattetAvSaerreglerForKraftforetak erLik true}

        val harOpplysningspliktForKraftverk =  opplysningspliktSdfGjeldende
           .forForekomsterAv{opplysningsplikt}
           .hvorMinstEnForekomst{erOmfattetAvSaerreglerForKraftforetak erLik true}

        // Kontroll som gir utslag på alle UP/SDF som er kraftverk i skatteplikt/opplysningsplikt
        // som oppgir ugyldig løpenummer på kraftverk i næringsspesifikasjonen (mellom 0-100 eller 1000-1200)
        naeringsspesifikasjon
      		.forForekomsterAv{spesifikasjonAvEnhetIVindkraftverk}
            .utslagFor {
    			val harKraftverkMedUgyldigLoepenummer = hvorMinstEnForekomst{(loepenummer er oppgitt) og
                                                                             (((loepenummer erStørreEnnEllerLik 1) og (loepenummer erMindreEnnEllerLik 100)) eller
                                                                              ((loepenummer erStørreEnnEllerLik 1000) og (loepenummer erMindreEnnEllerLik 1200)))}

                hvis((harSkattepliktForKraftverk eller harOpplysningspliktForKraftverk) og
                     ikke(harKraftverkMedUgyldigLoepenummer))
              {
                    betjeningsstrategiSettesTil(
                        ikke,
                        faktiskFeil)
                }
            }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {

       val oppgittEndringsaarsakIkkeErOverførtFraEktefelle = utgåtteSkattemeldingFelter {
           skattemeldingUtgått
           .forForekomsterAv(sti(obligasjonOgSertifikat, aarsakTilEndring))
           .harIngenForekomsterDer { aarsak erLik "overfortTilFraEktefelle" }
       }

        // Kontrollen slår ut hvis skattepliktige egenfastsetter tap over kr 200 000 på nytt obligasjonskort,
        // og har ikke oppgitt at det er overførst fra ektefelle/samboer
        // kontrollen gir merknad i SME

        utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .forForekomsterAv(obligasjonOgSertifikat)
            .utslagFor {
                val harDifferanseTap = hvorMinstEnForekomst {
