sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        // kontrollen gir utslag dersom skattyter har egenfastsatt ny pensjon
        // og har lagt til en av disse endringsårsakene på pensjon,
        // men har minst en forekomst av pensjon pr måned som IKKE har oppgitt noe skattemessig unntak eller dobbeltbeskatningsmetode
        utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .forForekomsterAv(pensjonsinntekt)
            .filtrer {
                forForekomsterAv(aarsakTilEndring)
                    .hvorMinstEnForekomst {
                        fastsatt(aarsak).finnesBlant(
                            "endretTilOpptjentIUtlandet",
                            "begrensetSkattepliktig",
                            "arbeidsinntektINorgeSomIkkeErSkattepliktig"
                        )
                    }
            }
            .utslagFor {
                val harEgenfastsattPensjonUtenSkattemessigUnntakOgUtenDobbeltbeskatningsmetode =
                    forForekomsterAv(perMaaned)
                        .hvorMinstEnForekomst {
                            (grunnlag(pensjonsinntektSomGruppe.beloepSomTall) ikkeEr oppgitt) og
                                (fastsatt(pensjonsinntektSomGruppe.beloepSomTall) er oppgitt) og
                                (fastsatt(pensjonsinntektSomGruppe.metodeVedDobbeltbeskatning) ikkeEr oppgitt) og
                                (
                                    fastsatt(pensjonsinntektSomGruppe.trygdeEllerSkattemessigUnntak).finnesIkkeBlant(
                                        "skattefriMedTrygdeavgiftspliktForHelsedel",
                                        "skattefriMedTrygdeavgiftspliktForPensjonsdel",
                                        "skattefriMedTrygdeavgiftsplikt",
                                        "skattefriUtenTrygdeavgiftsplikt",
                                        "skattefriKildeskattPaaPensjon",
                                        "skattefriMedTrygdeavgiftspliktForSykestoenadsdelForArbeidstakereUtsendtFraUSAOgCanada",
                                        "skattepliktigKildeskattPaaPensjon",
                                        "skattepliktigUtenTrygdeavgiftsplikt",
                                        "skattepliktigUtenTrygdeavgiftspliktForPensjonsdel",
                                        "skattepliktigUtenTrygdeavgiftspliktForHelsedel"

                                    )
                                    )
                        }
                hvis(ikke(harInnsending) og harEgenfastsattPensjonUtenSkattemessigUnntakOgUtenDobbeltbeskatningsmetode) {
