sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        // kontrollen gir utslag dersom skattyter har mer enn en bolig som er primærbolig (ser bort fra evt  boliger som er overført barn)
        skattemelding
            .fokusområde("Fokusområde")
            .forForekomsterAv{fastEiendom}
            .forForekomsterAv{fastEiendomSomFormuesobjekt}
            .utslagForFørste {

                val eierEiendom = hvorMinstEnForekomst {fastsatt(eierandel) erStørreEnn 0 }

                val primaerbolig = skattemelding
                    .forForekomsterAv{fastEiendom}
                    .forForekomsterAv{fastEiendomSomFormuesobjekt}
                    .filtrer { (fastsatt(boligbruk) erLik "primaerbolig") og (fastsatt(overfoertFraBarn.norskPersonidentifikator) ikkeEr oppgitt) }

                val primaerboligIFlerboligbygg = skattemelding
                    .forForekomsterAv{fastEiendom}
                    .forForekomsterAv{fastEiendomSomFormuesobjekt}
                    .filtrer { fastsatt(overfoertFraBarn.norskPersonidentifikator) ikkeEr oppgitt }
                    .forForekomsterAv{useksjonertBoenhet}
                    .filtrer { fastsatt(boligbruk) erLik "primaerbolig" }

                val primarboligForekomster = (primaerbolig + primaerboligIFlerboligbygg).antall

                hvis(eierEiendom og (primarboligForekomster erStørreEnn 1)) {
                    betjeningsstrategiSettesTil(
                        ikke,
                        merknadStandard
                    )
                }
            }
        }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {

      val harTrygdefritakForPensjonsdelIPeriodelageret = periodelager
            .forForekomsterAv { perioder }
            .forForekomsterAv { trygdefritaksperiode }
            .hvorMinstEnForekomst {fritak erLik "fritakForPensjonsdel"}

       // Kontrollen gir utslag og merknad dersom skattyter har perioder med trygdefritak på pensjonsdel i system Skatteplikt (periodelageret)
       // og dersom skattyter har innrapportert trygdefritak på pensjonsdel (har trygdefritak pensjonsdel i grunnlag)
       // og har endret det til trygdefritak på helsedel (har trygdefritak helsedel i fastsatt)
       skattemelding
            .forForekomsterAv{arbeidsgiver}
            .utslagFor {

                val harRegistrertTrygdefritakHelsedel =
                    forForekomsterAv{samledeYtelserFraArbeidsgiverPerBehandlingsart}
                        .hvorMinstEnForekomst {
                          (grunnlag(beloep.beloep) er oppgitt) og
                          (grunnlag(beloep.trygdeEllerSkattemessigUnntak).finnesBlant(
                              "skattepliktigUtenTrygdeavgiftspliktForHelsedel",
                              "skattefriMedTrygdeavgiftspliktForPensjonsdel")) og
                          (fastsatt(beloep.trygdeEllerSkattemessigUnntak).finnesBlant(
                              "skattepliktigUtenTrygdeavgiftspliktForPensjonsdel",
                              "skattefriMedTrygdeavgiftspliktForHelsedel",
                              "skattefriMedTrygdeavgiftspliktForSykestoenadsdelForArbeidstakereUtsendtFraUSAOgCanada"))
                        }

                hvis(harTrygdefritakForPensjonsdelIPeriodelageret og harRegistrertTrygdefritakHelsedel) {
                    betjeningsstrategiSettesTil(
                        ikke, merknadStandard
                    )
                }
            }
        }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig)
    .hvis({skattepliktGjeldende
            .forForekomsterAv { skatteplikt }
            .hvorMinstEnForekomst { personligSkattesubjekt.alderIInntektsaar erMindreEnn 70 }})
    {
        val partsnr = skattepliktGjeldende
            .forForekomsterAv { skatteplikt }
            .verdiFor { partsnummer }

       val kontrollisteOppslag = hentKontrolliste(
            "kontrolliste - Medlemskap folketrygden",
            listeAv(partsnr))
            .forForekomsterAv { verdi }

        val alleVerdierFraKontrolliste = kontrollisteOppslag
            .alleVerdier

        val verdiDekningFraKontrolliste = kontrollisteOppslag
            .verdierForListe { tekstVerdier("verdi8", "verdi9", "verdi10", "verdi11", "verdi12", "verdi13") }

        val sumTrygdeavgiftLoennsinntekt = beregnetSkattDokument
            .summer { grunnlag(beregnetSkatt.skattOgAvgift.trygdeavgiftAvLoennsinntekt.beloep) }

        //kontrollen gir utslag og merknad dersom skattyters partsnr finnes i kontrollisten
        //og de i kontrollisten har dekning = FTL_2-9_1_ledd_a/FTL_2-9_2_ld_jfr_1a/FTL_2-7a_2_ledd_b/FTL_2-7_3_ledd_b
        //(Nav Internasjonalt har fattet et vedtak om frivillig/pliktig medlemskap i norsk folketrygd helsedel)
        //gir utslag hvis SP har registrert fritak for helsedel
        //kun utslag hvis beregnet trygdeavgift av lønn i GLD
        //ikke utslag hvis lønnstaker er 70 år eller eldre
        utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .forForekomsterAv(arbeidsgiver)
            .utslagFor {
                val harEndretTilTrygdefritakHelsedel =
                    forForekomsterAv(samledeYtelserFraArbeidsgiverPerBehandlingsart)
                        .hvorMinstEnForekomst {
                            (fastsatt(beloepSomGruppe.beloepSomTall) er oppgitt) og
                                (
                                    fastsatt(beloepSomGruppe.trygdeEllerSkattemessigUnntak).finnesBlant(
                                        "skattepliktigUtenTrygdeavgiftspliktForHelsedel",
                                        "skattefriMedTrygdeavgiftspliktForPensjonsdel"
                                    )
                                    )
                        }

                hvis(
                    (verdiDekningFraKontrolliste.finnesBlant(
                        "FTL_2-9_1_ledd_a",
                        "FTL_2-9_2_ld_jfr_1a",
                        "FTL_2-7a_2_ledd_b",
                        "FTL_2-7_3_ledd_b"
                    )) og
                        (alleVerdierFraKontrolliste.antall erStørreEnn 0) og
                        harEndretTilTrygdefritakHelsedel og
                        (sumTrygdeavgiftLoennsinntekt erStørreEnn 0)
                ) {
                    betjeningsstrategiSettesTil(
                        ikke, merknadStandard, 1
                    )
                }
            }
        }
    }

