        //kontrollen gir utslag dersom skattyters partsnr finnes i kontrollisten
        //og de i kontrollisten har dekning = FTL_2-9_1_ledd_c/FTL_2-7_3_ledd_a/FTL_2-9_2_ld_jfr_1c/Full
        //(Nav Internasjonalt har fattet et vedtak om fullt medlemskap i norsk folketrygd (både hele eller deler av året))
        //gir utslag hvis skattyter har registrert lønn uten trygdeavgift, uten trygdeavgift for helsedel, eller uten trygdeavgift for pensjonsdel
        //og trygdeEllerSkattemessigUnntak er ulik på grunnlag og fastsatt
        //kun utslag hvis beregnet trygdeavgift av lønn i GLD
        utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .forForekomsterAv(arbeidsgiver)
            .utslagFor {
                val harEndretTilTrygdefritak = forForekomsterAv(samledeYtelserFraArbeidsgiverPerBehandlingsart)
                    .hvorMinstEnForekomst {
                        (grunnlag(beloepSomGruppe.beloepSomTall) er oppgitt) og
                            (fastsatt(beloepSomGruppe.beloepSomTall) er oppgitt) og
                            (
                                fastsatt(beloepSomGruppe.trygdeEllerSkattemessigUnntak).finnesBlant(
                                    "skattepliktigUtenTrygdeavgiftsplikt",
                                    "skattepliktigUtenTrygdeavgiftspliktForPensjonsdel",
                                    "skattepliktigUtenTrygdeavgiftspliktForHelsedel",
                                    "skattefriMedTrygdeavgiftspliktForHelsedel",
                                    "skattefriMedTrygdeavgiftspliktForPensjonsdel",
                                    "skattefriMedTrygdeavgiftspliktForSykestoenadsdelForArbeidstakereUtsendtFraUSAOgCanada",
                                    "skattefriUtenTrygdeavgiftsplikt"
                                )
                                ) og
                            (fastsatt(beloepSomGruppe.trygdeEllerSkattemessigUnntak) erUlik grunnlag(
                                beloepSomGruppe.trygdeEllerSkattemessigUnntak
                            ))
                    }

                hvis(
                    (verdiDekningFraKontrolliste.finnesBlant(
                        "FTL_2-9_1_ledd_c",
                        "FTL_2-7_3_ledd_a",
                        "FTL_2-9_2_ld_jfr_1c",
                        "Full"
                    )) og
                        (alleVerdierFraKontrolliste.antall erStørreEnn 0) og
                        harEndretTilTrygdefritak og
                        (sumTrygdeavgiftLoennsinntekt erStørreEnn 0)
                ) {
                    betjeningsstrategiSettesTil(
                        ikke, merknadStandard, 1
                    )
                }
            }
        }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig)
    {
        val partsnr = skattepliktGjeldende
            .forForekomsterAv { skatteplikt }
            .verdiFor { partsnummer }

        val kontrollisteOppslag = hentKontrolliste(
            "kontrolliste - Medlemskap folketrygden",
            listeAv(partsnr))
            .forForekomsterAv { verdi }

        val alleVerdierFraKontrolliste = kontrollisteOppslag
            .alleVerdier

        val verdiDekningFraKontrolliste = kontrollisteOppslag
            .verdierForListe { tekstVerdier("verdi8", "verdi9", "verdi10", "verdi11", "verdi12", "verdi13") }

        val sumTrygdeavgiftPensjonsinntekt = beregnetSkattDokument
            .summer { grunnlag(beregnetSkatt.skattOgAvgift.trygdeavgiftAvPensjonsinntekt.beloep) }

        //kontrollen gir utslag dersom skattyters partsnr finnes i kontrollisten
        //og de i kontrollisten har dekning = FTL_2-9_1_ledd_c/FTL_2-7_3_ledd_a/FTL_2-9_2_ld_jfr_1c/Full
        //(Nav Internasjonalt har fattet et vedtak om fullt medlemskap i norsk folketrygd (både hele eller deler av året))
        //Gir utslag hvis skattyter har registrett pensjon/uføretrygd med skattefriUtenTrygdeavgiftsplikt eller skattepliktigUtenTrygdeavgiftsplikt og den er ulik grunnlag
        //kun utslag hvis beregnet trygdeavgift av pensjonsinntekt i GLD
        utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .forForekomsterAv(pensjonsinntekt)
            .utslagFor {
                val harEndretTilTrygdefritak = forForekomsterAv(perMaaned)
                    .hvorMinstEnForekomst {
                        (grunnlag(pensjonsinntektSomGruppe.beloepSomTall) er oppgitt) og
                            (fastsatt(pensjonsinntektSomGruppe.beloepSomTall) er oppgitt) og
                            (
                                fastsatt(pensjonsinntektSomGruppe.trygdeEllerSkattemessigUnntak).finnesBlant(
                                    "skattefriUtenTrygdeavgiftsplikt",
                                    "skattepliktigUtenTrygdeavgiftsplikt"
                                )
                                ) og
                            (fastsatt(pensjonsinntektSomGruppe.trygdeEllerSkattemessigUnntak) erUlik grunnlag(
                                pensjonsinntektSomGruppe.trygdeEllerSkattemessigUnntak
                            ))
                    }

                hvis(
                    (verdiDekningFraKontrolliste.finnesBlant(
                        "FTL_2-9_1_ledd_c",
                        "FTL_2-7_3_ledd_a",
                        "FTL_2-9_2_ld_jfr_1c",
                        "Full"
                    )) og
                        (alleVerdierFraKontrolliste.antall erStørreEnn 0) og
                        harEndretTilTrygdefritak og
                        (sumTrygdeavgiftPensjonsinntekt erStørreEnn 0)
                ) {
                    betjeningsstrategiSettesTil(
                        ikke, merknadStandard, 1
                    )
                }
            }
        }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {

        val harTrygdefritakForHelsedeIPeriodelageret = periodelager
            .forForekomsterAv { perioder }
            .forForekomsterAv { trygdefritaksperiode }
            .hvorMinstEnForekomst {fritak erLik "fritakForHelsedel"}

        // Kontrollen gir utslag og merknad dersom skattyter har registrert trygdefritak på pensjonsdel
        // har perioder med trygdefritak på helsedel i system Skatteplikt (periodelageret)
        skattemelding
            .forForekomsterAv{arbeidsgiver}
            .utslagFor {

                val harRegistrertTrygdefritakPensjonsdel =
                    forForekomsterAv{samledeYtelserFraArbeidsgiverPerBehandlingsart}
                        .hvorMinstEnForekomst {
                          (grunnlag(beloep.beloep) er oppgitt) og
                          (grunnlag(beloep.trygdeEllerSkattemessigUnntak).finnesBlant(
                              "skattepliktigUtenTrygdeavgiftspliktForPensjonsdel",
                              "skattefriMedTrygdeavgiftspliktForHelsedel",
                              "skattefriMedTrygdeavgiftspliktForSykestoenadsdelForArbeidstakereUtsendtFraUSAOgCanada")) og
                          (fastsatt(beloep.trygdeEllerSkattemessigUnntak).finnesBlant(
                              "skattepliktigUtenTrygdeavgiftspliktForHelsedel",
                              "skattefriMedTrygdeavgiftspliktForPensjonsdel"))
                        }


                hvis(harTrygdefritakForHelsedeIPeriodelageret og harRegistrertTrygdefritakPensjonsdel) {
                    betjeningsstrategiSettesTil(
                        ikke, merknadStandard
                    )
                }
            }
        }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig)
    .hvis({skattepliktGjeldende
            .forForekomsterAv { skatteplikt }
            .hvorMinstEnForekomst { personligSkattesubjekt.alderIInntektsaar erMindreEnn 70 }})
    {
        val partsnr = skattepliktGjeldende
            .forForekomsterAv { skatteplikt }
