                    betjeningsstrategiSettesTil(
                        ikke, merknadStandard, 1
                    )
                }
            }
        }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig)
    .importer(AARSAK_TIL_ENDRING_OG_TRYGDE_ELLER_SKATTEMESSIG_UNNTAK) {

      (D_aarsakTilEndringEllerSkattepliktOgSkattemessigUnntak) ->

       val harTrygdefritakISkatteplikt = skattepliktGjeldende
          .forForekomsterAv { skatteplikt }
          .hvorMinstEnForekomst{personligSkattesubjekt.fritakForTrygdeavgiftsplikt erLik true}

       val harAntallDagerMedFulltTrygdefritakFraOOSSGjeldende = opplysningerOmSkattesubjektGjeldendeDokument
                .forForekomsterAv { opplysningerOmSkattesubjekt }
                .forForekomsterAv { informasjonOmTrygdeavtale }
                .hvorMinstEnForekomst {antallDagerFulltFritakFraTrygdeavgift er oppgitt}


        // Kontrollen gir utslag dersom SP i Skatteplikt eller OOSS mangler periode for fritak for aktuelt inntektsår
        // og SP registrerer endringsårsak "Jeg har fritak fra trygdeavgift"
        //   eller har "uten trygdeavgift" i felt trygde- og skattemessig unntak (trygdefritak) og grunnlag er ulik fastsatt
        //   eller legger til periode for fritak i kort tilleggsinformasjonVedOpphoerAvMedlemskapIFolketrygden under tema Andre forhold.
        // skal IKKE gi utslag hvis SP velger endringssårsak "Mitt medlemskap i folketrygden er opphørt"
        //   eller har registrert dato for opphør i kort under tema Andre forhold.
       skattemelding
            .utslagFor {

             val oppgittAarsakTilEndringLikFritakForTrygdeavgiftOgUlikOpphoerEllerHarTrygdemessigUnntak = D_aarsakTilEndringEllerSkattepliktOgSkattemessigUnntak
                 .forForekomsterAv { kort }
                 .hvorMinstEnForekomst { kortforekomst ->
                     kortforekomst.forForekomsterAv { aarsakTilEndring }
                                  .hvorMinstEnForekomst { (aarsak erLik "fritakDelvisFritakForTrygdeavgift") og
                                                          (aarsak erUlik "opphoerTrygdemedlemskap")} eller
                     kortforekomst.forForekomsterAv { kortMedEgenskaper }
                                  .hvorMinstEnForekomst {(fastsatt(trygdeEllerSkattemessigUnntak).finnesBlant(
                                "skattepliktigUtenTrygdeavgiftspliktForHelsedel",
                                "skattepliktigUtenTrygdeavgiftspliktForPensjonsdel",
                                "skattefriUtenTrygdeavgiftsplikt",
                                "skattepliktigUtenTrygdeavgiftsplikt") ) og
                                 (fastsatt(trygdeEllerSkattemessigUnntak) erUlik grunnlag(trygdeEllerSkattemessigUnntak))}
                 }

             val tilleggsinfoVedOpphoerAvMedlemskapIFolketrygdenPeriodeForFritak = skattemelding
                 .forForekomsterAv{tilleggsinformasjonVedOpphoerAvMedlemskapIFolketrygden}
                 .hvorMinstEnForekomst{(fastsatt(startdatoForFritakFraTrygdeavgiftsplikt) er oppgitt) eller
                                       (fastsatt(sluttdatoForFritakFraTrygdeavgiftsplikt) er oppgitt)}

             val tilleggsinfoVedOpphoerAvMedlemskapIFolketrygdenDatoForOpphoer = skattemelding
                .forForekomsterAv{tilleggsinformasjonVedOpphoerAvMedlemskapIFolketrygden}
                .hvorMinstEnForekomst{fastsatt(startdatoForOpphoer) er oppgitt}

            val kortTilleggsinformasjonVedOpphoerAvMedlemskapIFolketrygden = forForekomsterAv{tilleggsinformasjonVedOpphoerAvMedlemskapIFolketrygden}


                hvis(ikke(harTrygdefritakISkatteplikt) og
                     ikke(harAntallDagerMedFulltTrygdefritakFraOOSSGjeldende) og
                     (oppgittAarsakTilEndringLikFritakForTrygdeavgiftOgUlikOpphoerEllerHarTrygdemessigUnntak eller
                     tilleggsinfoVedOpphoerAvMedlemskapIFolketrygdenPeriodeForFritak) og
                     ikke(tilleggsinfoVedOpphoerAvMedlemskapIFolketrygdenDatoForOpphoer))
                {
                    betjeningsstrategiSettesTil(
                        ikke, merknadITopp, ekspander,
                        Lenke settesTil kortTilleggsinformasjonVedOpphoerAvMedlemskapIFolketrygden
                    )
                }
            }
        }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig)
    {

     // Kontrollen gir utslag dersom skattyter har registrert tap eller gevinst ved realisasjon av eiendom i land hvor realisasjon av eiendom er unntatt fra beskatning - ingen beløpsgrense
     // skal ikke gi utslag dersom skattepliktig har valgt Ikke skattepliktig eller trygdepliktig under Trygde- og skattemessige unntak
       skattemelding
         .forForekomsterAv{fastEiendom}
         .filtrer{
           (fastsatt(landkodeBeliggenhet) er oppgitt) og
           (fastsatt(landkodeBeliggenhet).finnesBlant("US","ME","PH","MY","BJ","BQ","BA","EG","CI","ID",
                                               "IL","IT","KE","CN","HR","MA","MX","PK","LK","SX","KR","TN","ZW"))
        }
        .forForekomsterAv{fastEiendomSomRealisasjonsobjekt}
        .utslagFor {

           val harTapVedRealisasjonAvEiendomILandUnntattBeskatning = hvorMinstEnForekomst{(fastsatt(tapVedRealisasjonAvFastEiendom.fradragsberettigetBeloep.beloep) erStørreEnn 0) og
                                                                                          (fastsatt(tapVedRealisasjonAvFastEiendom.fradragsberettigetBeloep.trygdeEllerSkattemessigUnntak erUlik "skattefriUtenTrygdeavgiftsplikt"))}

           val harGevinstVedRealisasjonAvEiendomILandUnntattBeskatning = hvorMinstEnForekomst{(fastsatt(gevinstVedRealisasjonAvFastEiendom.beloep) erStørreEnn 0) og
                                                                                              (fastsatt(gevinstVedRealisasjonAvFastEiendom.trygdeEllerSkattemessigUnntak erUlik "skattefriUtenTrygdeavgiftsplikt"))}

              hvis(harTapVedRealisasjonAvEiendomILandUnntattBeskatning eller
                   harGevinstVedRealisasjonAvEiendomILandUnntattBeskatning)
               {
                betjeningsstrategiSettesTil(
                    ikke, merknadMangel,skjulerSkatteberegning)
              }
            }
    }

