sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        val harSkattepliktPaaLoennFraDenNorskeStatOpptjentIUtlandet = skattepliktGjeldende
            .forForekomsterAv { skatteplikt }
            .forForekomsterAv { personligSkattesubjekt.saerskiltSkatteplikt }
            .hvorMinstEnForekomst { type erLik "skattepliktPaaLoennFraDenNorskeStatOpptjentIUtlandet" }

        // kontrollen gir utslag dersom skattyter har særskilt skatteplikt på lønn fra den norske stat hele eller deler av året
        // og har en eller annen form for gjeldsrente eller gjeld
        skattemelding
            .utslagFor {

            val harGjeldEllerGjeldsrenterAnnenGjeld = forForekomsterAv{annenGjeld}
               .hvorMinstEnForekomst{((gjeldsbeloep.beloep erStørreEnn 0) og (gjeldsbeloep.trygdeEllerSkattemessigUnntak erUlik "skattefriUtenTrygdeavgiftsplikt")) eller
                                     ((paaloepteRenter.fradragsberettigetBeloep.beloep erStørreEnn 0) og (paaloepteRenter.fradragsberettigetBeloep.trygdeEllerSkattemessigUnntak erUlik "skattefriUtenTrygdeavgiftsplikt"))}

            val harGjeldEllerGjeldsrenterInkassoselskapOgBetalingsformidler = forForekomsterAv{inkassoselskapOgBetalingsformidler}
               .hvorMinstEnForekomst{((gjeld.beloep erStørreEnn 0) og (gjeld.trygdeEllerSkattemessigUnntak erUlik "skattefriUtenTrygdeavgiftsplikt")) eller
                                     ((paaloepteRenter.fradragsberettigetBeloep.beloep erStørreEnn 0) og (paaloepteRenter.fradragsberettigetBeloep.trygdeEllerSkattemessigUnntak erUlik "skattefriUtenTrygdeavgiftsplikt"))}

            val harGjeldEllerGjeldsrenterAnnetFinansprodukt = forForekomsterAv{annetFinansprodukt}
               .hvorMinstEnForekomst{(gjeldPaaAnnetFinansprodukt.beloep erStørreEnn 0) og (gjeldPaaAnnetFinansprodukt.trygdeEllerSkattemessigUnntak erUlik "skattefriUtenTrygdeavgiftsplikt")}

            val harGjeldEllerGjeldsrenterKapitalisertFesteavgift = forForekomsterAv{kapitalisertFesteavgift}
               .hvorMinstEnForekomst{(gjeldsfradragForKapitalisertFesteavgift.beloep erStørreEnn 0) og (gjeldsfradragForKapitalisertFesteavgift.trygdeEllerSkattemessigUnntak erUlik "skattefriUtenTrygdeavgiftsplikt")}

            val harGjeldEllerGjeldsrenterKonto = forForekomsterAv{konto}
               .hvorMinstEnForekomst{((gjeld.beloep erStørreEnn 0) og (gjeld.trygdeEllerSkattemessigUnntak erUlik "skattefriUtenTrygdeavgiftsplikt")) eller
                                     ((paaloepteRenter.fradragsberettigetBeloep.beloep erStørreEnn 0) og (paaloepteRenter.fradragsberettigetBeloep.trygdeEllerSkattemessigUnntak erUlik "skattefriUtenTrygdeavgiftsplikt")) eller
                                     ((betalteMisligholdteRenter.beloep erStørreEnn 0) og (betalteMisligholdteRenter.trygdeEllerSkattemessigUnntak erUlik "skattefriUtenTrygdeavgiftsplikt"))}

            val harGjeldEllerGjeldsrenterSkyldigRestskatt = forForekomsterAv{skyldigRestskatt}
               .hvorMinstEnForekomst{((beloep.beloep erStørreEnn 0) og (beloep.trygdeEllerSkattemessigUnntak erUlik "skattefriUtenTrygdeavgiftsplikt")) eller
                                     ((betalteForsinkelsesrenter.fradragsberettigetBeloep.beloep erStørreEnn 0) og (betalteForsinkelsesrenter.fradragsberettigetBeloep.trygdeEllerSkattemessigUnntak erUlik "skattefriUtenTrygdeavgiftsplikt"))}


            hvis(harSkattepliktPaaLoennFraDenNorskeStatOpptjentIUtlandet og
                (harGjeldEllerGjeldsrenterAnnenGjeld eller harGjeldEllerGjeldsrenterInkassoselskapOgBetalingsformidler eller harGjeldEllerGjeldsrenterAnnetFinansprodukt eller
                 harGjeldEllerGjeldsrenterKapitalisertFesteavgift eller harGjeldEllerGjeldsrenterKonto eller harGjeldEllerGjeldsrenterSkyldigRestskatt))
