            .hvorMinstEnForekomst { (personligSkattesubjekt.skattepliktTilSvalbard erLik "global") eller (doedsbo.skattepliktTilSvalbard erLik "global") }

        val eiendommerMedUtleie = skattemelding
           .forForekomsterAv{fastEiendom}
           .filtrer{eiendomsforekomster ->
                eiendomsforekomster
                  .forForekomsterAv{fastEiendomSomUtleieobjekt}
                  .hvorMinstEnForekomst { (nettoLeieinntektForAndel.beloep er oppgitt) eller
                                          (bruttoLeieinntektVedRegnskapsbehandletUtleie.beloep er oppgitt) eller
                                          (bruttoLeieinntektVedSjablongbehandletUtleie.beloep er oppgitt)}
           }
           .verdierFor{fastsatt(sergEiendomsidentifikator)}

        // Kontrollen gir utslag dersom skattyter har globalskatteplikt på Svalbard
        // og har primærbolig i Norge,
        // men ikke har oppgitt utleie
        // kontrollen gir merknad i SME
      skattemelding
        .fokusområde("Fokusområde")
        .forForekomsterAv{fastEiendom}
        .filtrer{((fastsatt(landkodeBeliggenhet) erLik "NO") eller (fastsatt(landkodeBeliggenhet) ikkeEr oppgitt)) og
                 (fastsatt(sergEiendomsidentifikator).finnesIkkeBlant(eiendommerMedUtleie))}
        .forForekomsterAv{fastEiendomSomFormuesobjekt}
        .utslagFor {

          val harPrimaerboligUtenUtleie = hvorMinstEnForekomst{boligbruk erLik "primaerbolig"}

          val harPrimaerboligFlerboligbyggUtenUtleie = forForekomsterAv{useksjonertBoenhet}
             .hvorMinstEnForekomst{boligbruk erLik "primaerbolig"}

          hvis(harSkattepliktTilSvalbard og
               (harPrimaerboligUtenUtleie eller harPrimaerboligFlerboligbyggUtenUtleie)
              ) {
            betjeningsstrategiSettesTil(ikke, merknadTips)
          }
        }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {

      val harUtslagPaaKontroll =
         utslagPåKontroll("VALUTAREGISTER_KRYPTO_KJENT_FORHANDLER_MERKNAD_TOPP") eller
         utslagPåKontroll("VALUTAREGISTER_KRYPTO_KJENT_FORHANDLER_MERKNAD_TIPS") eller
         utslagPåKontroll("VALUTAREGISTER_KRYPTO_KJENT_FORHANDLER_IKKE_MERKNAD") eller
         utslagPåKontroll("VALUTAREGISTER_KRYPTO_MERKNAD_TIPS") eller
         utslagPåKontroll("VALUTAREGISTER_KRYPTO_MERKNAD_TOPP") eller
         utslagPåKontroll("VALUTAREGISTER_KRYPTO_IKKE_MERKNAD") eller
         utslagPåKontroll("VALUTAREGISTER_KRYPTO_KJENT_FORHANDLER_MILD_MERKNAD_TOPP") eller
         utslagPåKontroll("VALUTAREGISTER_KRYPTO_KJENT_FORHANDLER_MILD_MERKNAD_TIPS") eller
         utslagPåKontroll("VALUTAREGISTER_KRYPTO_KJENT_FORHANDLER_MILD_IKKE_MERKNAD") eller
         utslagPåKontroll("KRYPTO_OPPLYSNINGER_FIRI_TIPS") eller
         utslagPåKontroll("KRYPTO_OPPLYSNINGER_FIRI_IKKE_MERKNAD") eller
         utslagPåKontroll("VIRTUELL_VALUTA_KRYPTOVALUTA_GEV_TAP_I_FJOR_IKKE_MERKNAD") eller
         utslagPåKontroll("VIRTUELL_VALUTA_KRYPTOVALUTA_GEV_TAP_I_FJOR")

     val sumTapKryptovaluta = skattemelding
       .forForekomsterAv{virtuellEiendel}
       .summer{skattepliktigTapVedRealisasjonAvVirtuellEiendel.fradragsberettigetBeloep.beloep}

        // Kontrollen gir utslag og merknad når skattyter har lagt inn tap på svindel som er lik
        // tap på virtuell eiendel, med slingringsmonn på 10%
        // skal ikke gi utslag hvis det er utslag på en av de gitte kontrollene
        skattemelding
           .forForekomsterAv{tapVedSvindel}
           .utslagFor {

         val sumTapVedSvindel =
             summer{fradragsberettigetBeloepTaptVedSvindel.fradragsberettigetBeloep.beloep}

         val sumTapKryptoGangerProsent =
             sumTapKryptovaluta * 10.prosent

         val sumTapSvindelGangerProsent =
             sumTapVedSvindel * 10.prosent

          val differanseKryptoOgSvindel =
             differanse (sumTapKryptovaluta , sumTapVedSvindel)

           hvis((sumTapKryptovaluta erStørreEnn 0) og
                 (sumTapVedSvindel erStørreEnn 0) og
                 (differanseKryptoOgSvindel erStørreEnnEllerLik 0) og
                ((differanseKryptoOgSvindel erMindreEnn sumTapKryptoGangerProsent) eller
                 (differanseKryptoOgSvindel erMindreEnn sumTapSvindelGangerProsent)) og
                ikke(harUtslagPaaKontroll)
                )
                {
                    betjeningsstrategiSettesTil(ikke, merknadTips)
                }
            }
        }

