sakstype(Personlig)
    .kontekst(ValideringSkattepliktig)
    {
        val harGlobalSkatteplikt = skattepliktGjeldende
            .forForekomsterAv { skatteplikt }
            .hvorMinstEnForekomst { personligSkattesubjekt.skattepliktTilNorge erLik "global" }

        val EOESLandkoder = aktiveLandkoder_KL
            .forForekomsterAv { kode }
            .filtrer { kodetillegg.medlemAvEoes erLik "Ja" }
            .verdierFor { tekniskNavn }

        val bosattIEOESLand = partInfo
            .forForekomsterAv { partInfo }
            .forForekomsterAv { postadresseIUtlandet }
            .hvorMinstEnForekomst { landkode.finnesBlant(EOESLandkoder) }

        val sumTrygdeaviftIGrunnlag = beregnetSkattDokument
            .summer { grunnlag(beregnetSkatt.skattOgAvgift.sumTrygdeavgift.beloep) }

        val harOppgittTilleggsinfoForOpphoer = skattemelding
            .hvorMinstEnForekomst { tilleggsinformasjonVedOpphoerAvMedlemskapIFolketrygden er oppgitt }

        // Kontrollen gir utslag hvis skattepliktig er globalskattepliktig
        // OG skattepliktig er bosatt i EØS-land (mangler funksjon for å sjekke på partinfo postadresse i utlandet, og sjekke om land  i kodeliste EØS)
        // OG mottaker av uføre/pensjon fra minst en av disse utbetalerne som ifølge rundskriv til EØS-forordning er omfattet av koordineringsreglene innen EØS:
        //       Norsk folketrygd/NAV
        //       Statens pensjonskasse (rapportering fra NAV)
        //       Pensjonstrygden for fiskere - GARANTIKASSEN FOR FISKERE org.nr. 974652382
        //       Pensjonstrygden for sjømenn - org.nr. 940415683/974707055 (eventuelt rapportering fra NAV)
        //       og/eller Pensjonsordning for sykepleiere - orgn.nr 967696676 / KLP FORSIKRINGSSERVICE AS org.nr. 987 381 930
        // OG har påstand om trygdemessig unntak ved minst en handling
        //       Endringsårsak for at medlemskap i folketrygden har opphørt, eller endringsårsak fritak fra trygdeavgift,
        //       eller oppgitt tillggsinfo for opphør
        //       eller valgt trygdemessig unntak m/u ikke skattepliktig)
        // skal kun gi utslag hvis det er beregnet trygdeavgift i grunnlag/utkast
        skattemelding
            .fokusområde("Fokusområde")
            .forForekomsterAv{pensjonsinntekt}
            .utslagForFørste {

            val SumNorskPensjonFraoffentligMedTrygdemessigUnntak = filtrer {
                    ((fastsatt(landkode) erLik "NO") eller (fastsatt(landkode) ikkeEr oppgitt)) og
                    (fastsatt(utbetalersOrganisasjonsnummer).finnesBlant("995277670", "974652382", "940415683", "974707055", "987381930", "967696676")) }
                .forForekomsterAv{perMaaned}
                .filtrer {(fastsatt(pensjonsinntekt.trygdeEllerSkattemessigUnntak).finnesBlant(
                            "skattefriMedTrygdeavgiftspliktForHelsedel",
                            "skattefriMedTrygdeavgiftspliktForPensjonsdel",
                            "skattefriUtenTrygdeavgiftsplikt",
                            "skattefriMedTrygdeavgiftspliktForSykestoenadsdelForArbeidstakereUtsendtFraUSAOgCanada",
                            "skattepliktigUtenTrygdeavgiftsplikt",
                            "skattepliktigUtenTrygdeavgiftspliktForPensjonsdel",
                            "skattepliktigUtenTrygdeavgiftspliktForHelsedel")) og
                          (grunnlag(pensjonsinntekt.trygdeEllerSkattemessigUnntak) erUlik fastsatt(pensjonsinntekt.trygdeEllerSkattemessigUnntak))
                }
                .summer { fastsatt(pensjonsinntekt.beloep) }

            val SumNorskPensjonFraoffentlig = filtrer {
                    ((fastsatt(landkode) erLik "NO") eller (fastsatt(landkode) ikkeEr oppgitt)) og
                    (fastsatt(utbetalersOrganisasjonsnummer).finnesBlant("995277670", "974652382", "940415683", "974707055","987381930", "967696676"))
                }
                .summer { fastsatt(pensjonsinntektPrSkattleggingsperiode.beloep) }

            val harOppgittEndringsaarsakOpphoerMedlemskapEllerTrygdefritak = forForekomsterAv{aarsakTilEndring}
                    .hvorMinstEnForekomst {fastsatt(aarsak).finnesBlant("opphoerTrygdemedlemskap", "fritakDelvisFritakForTrygdeavgift")}

            hvis(
                harGlobalSkatteplikt og
                bosattIEOESLand og
               ((SumNorskPensjonFraoffentligMedTrygdemessigUnntak erStørreEnn 0) eller
                ((SumNorskPensjonFraoffentlig erStørreEnn 0) og (harOppgittEndringsaarsakOpphoerMedlemskapEllerTrygdefritak eller harOppgittTilleggsinfoForOpphoer))) og
               (sumTrygdeaviftIGrunnlag erStørreEnn 0)
            )
            {
                betjeningsstrategiSettesTil(
                    ikke, merknadInformasjon
                )
            }
        }
        }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        // kontrollen gir utslag dersom skattyter har oppgitt trygde- eller skattemessig unntak på lønn eller pensjon
        // men ikke har lagt til endringsårsak fritakDelvisFritakForTrygdeavgift eller opphoerTrygdemedlemskap på lønn eller pensjon
        utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .fokusområde("Fokusområde")
            .forForekomsterAv(gruppe(arbeidsgiver, pensjonsinntekt))
            .filtrer {
                forForekomsterAv(aarsakTilEndring)
                    .harIngenForekomsterDer {
                        fastsatt(aarsak).finnesBlant(
                            "fritakDelvisFritakForTrygdeavgift",
                            "opphoerTrygdemedlemskap"
                        )
                    }
            }
            .utslagFor {
                val loennSomErOppgittMedTrygdemessigUnntak =
                    forForekomsterAv(samledeYtelserFraArbeidsgiverPerBehandlingsart)
                        .filtrer { fastsatt(beloepSomGruppe.beloepSomTall) er oppgitt }
                        .hvorMinstEnForekomst {
                            (
                                fastsatt(beloepSomGruppe.trygdeEllerSkattemessigUnntak).finnesBlant(
                                    "skattepliktigUtenTrygdeavgiftsplikt",
                                    "skattefriUtenTrygdeavgiftsplikt"
                                )
                                ) og
                                (grunnlag(beloepSomGruppe.trygdeEllerSkattemessigUnntak) ikkeEr oppgitt)
                        }

                val pensjonSomErOppgittMedTrygdemessigUnntak = forForekomsterAv(perMaaned)
                    .filtrer { fastsatt(pensjonsinntektSomGruppe.beloepSomTall) er oppgitt }
                    .hvorMinstEnForekomst {
                        (
                            fastsatt(pensjonsinntektSomGruppe.trygdeEllerSkattemessigUnntak).finnesBlant(
                                "skattepliktigUtenTrygdeavgiftsplikt",
                                "skattefriUtenTrygdeavgiftsplikt"
                            )
                            ) og
                            (grunnlag(pensjonsinntektSomGruppe.trygdeEllerSkattemessigUnntak) ikkeEr oppgitt)
                    }

                hvis(ikke(harInnsending) og loennSomErOppgittMedTrygdemessigUnntak eller pensjonSomErOppgittMedTrygdemessigUnntak) {
                    betjeningsstrategiSettesTil(
                        ikke, merknadStandard, 1
                    )
                }
            }
        }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {

        val harKrevdKreditfradragPaaInntektEllerFormue = utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .forAlleForekomster
            .hvorMinstEnForekomst { metodeVedDobbeltbeskatning erLik "kredit" }
        }

        // kontrollen gir utslag og merknad dersom  det er kreditfradrag til fremføring fra en av de 2 foregående årene
        // gir IKKE utslag dersom skattyter har lagt til kredit som metod på minst en inntekt eller formue
