                        (skattepliktigUtbytte.trygdeEllerSkattemessigUnntak erLik "skattefriUtenTrygdeavgiftsplikt")
                    }


                hvis(erUtenriksTjenestemann og harAksjeUtbytte)
                {
                    betjeningsstrategiSettesTil(ikke, merknadInformasjon)
                }
            }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        val erUtenriksTjenestemann = skattepliktGjeldende
            .forForekomsterAv { skatteplikt }
            .forForekomsterAv { personligSkattesubjekt.saerskiltSkatteplikt }
            .hvorMinstEnForekomst { type erLik "skattepliktVedUtenriksoppholdINorskStatstjenesteEllerNato" }

        val harOppholdUnderYrkesOgTjenestereiseIUtlandet = utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .forForekomsterAv(oppholdUnderYrkesOgTjenestereise)
            .hvorMinstEnForekomst { (landkode er oppgitt) og (landkode erUlik "NO") }
        }

        val harPendleroppholdIUtlandet = utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .forForekomsterAv(pendleropphold)
            .hvorMinstEnForekomst { (landkode er oppgitt) og (landkode erUlik "NO") }
        }

        //kontrollen gir utslag dersom skattyter har særskilt skatteplikt som utenriks tjenestemann i skatteplikt
        //og har kort for oppholdUnderYrkesOgTjenestereise eller pendleropphold med opphold i utlandet,
        //eller har kort for arbeidsreiser
        //men ikke har fylt ut andel av totale merkostnader i utlandet i kort for arbeidsoppholdUtenforHjemmet
        utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .fokusområde("Fokusområde")
            .forForekomsterAv(gruppe(arbeidsoppholdUtenforHjemmet, reiserIArbeidsforhold))
            .utslagFor {

                val harIkkeFyltUtAndelAvTotalenSomGjelderUtlandetMerkostnader = hvorMinstEnForekomst {
                    (fradragForMerkostnadVedArbeidsoppholdUtenforHjemmetSomGruppe.fradragsberettigetBeloep.beloepSomTall erStørreEnn 0) og
                        (fradragForMerkostnadVedArbeidsoppholdUtenforHjemmetSomGruppe.heravAndelTilordnetSaerskiltSkattepliktVedArbeidsoppholdIUtlandet.beloepSomTall erLik 0)
                }

                val harIkkeFyltUtAndelAvTotalenSomGjelderUtlandetArbeidsreise = hvorMinstEnForekomst {
                    (reisefradragSomGruppe.fradragsberettigetBeloep.beloepSomTall erStørreEnn 0) og
                        (reisefradragSomGruppe.heravAndelTilordnetSaerskiltSkattepliktVedArbeidsoppholdIUtlandet.beloepSomTall erLik 0)
                }

                hvis(
                    erUtenriksTjenestemann og
                        (((harOppholdUnderYrkesOgTjenestereiseIUtlandet eller harPendleroppholdIUtlandet) og harIkkeFyltUtAndelAvTotalenSomGjelderUtlandetMerkostnader) eller
                            harIkkeFyltUtAndelAvTotalenSomGjelderUtlandetArbeidsreise)
                )
                {
                    betjeningsstrategiSettesTil(ikke, merknadInformasjon)
                }
            }
        }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig)
    .hvis({ skattepliktGjeldende
              .forForekomsterAv { skatteplikt }
              .hvorMinstEnForekomst { personligSkattesubjekt.skattepliktTilNorge erLik "begrenset" }
    })
    .importer(SLUTTDATO_I_INNTEKTSAARET) {
      (D_sluttdatoIInntektsaaret) ->

        val sluttdatoIInntektsaaret = D_sluttdatoIInntektsaaret

        val harSaerskiltSkattepliktUtenrikstjenestemann = skattepliktGjeldende
           .forForekomsterAv { skatteplikt }
           .forForekomsterAv { personligSkattesubjekt.saerskiltSkatteplikt }
           .hvorMinstEnForekomst { type erLik "skattepliktVedUtenriksoppholdINorskStatstjenesteEllerNato" }

        val uteITjenesteVedSluttenAvAaret = periodelager
           .forForekomsterAv { perioder }
           .forForekomsterAv { arbeidsforholdsperiode }
           .hvorMinstEnForekomst{(tilOgMedDato ikkeEr oppgitt) eller (tilOgMedDato erEtter sluttdatoIInntektsaaret) }

        // kontrollen skal kun kjøre hvis skattyter har begrenset skatteplikt
        // kontrollen gir utslag dersom skattyter har særskilt skatteplikt som utenriks tjenestemann
        // og sluttdato for arbeidsoperiode enten ikke er oppgitt eller den er senere enn 31.12. i inntektsåret (er ute i tjeneste ved utgangen av året)
        // og skattyter har egenfastsatt formue som er skattepliktig - innskudd, kjøretøy, eiendom i utlandet
        skattemelding
            .fokusområde("Fokusområde")
            .forForekomsterAv{grupper(kjoeretoey,konto, fastEiendom.fastEiendomSomFormuesobjekt)}
            .utslagFor {

                val harOppgittFormueKjoeretoey = skattemelding
                   .forForekomsterAv{kjoeretoey}
                   .filtrer {fastsatt(formuesverdiForFormuesandelForKjoeretoey.trygdeEllerSkattemessigUnntak).finnesIkkeBlant(
                        "skattefriMedTrygdeavgiftspliktForHelsedel",
                        "skattefriMedTrygdeavgiftspliktForPensjonsdel",
                        "skattefriMedTrygdeavgiftsplikt",
                        "skattefriKildeskattPaaPensjon",
                        "skattefriUtenTrygdeavgiftsplikt",
                        "skattefriMedTrygdeavgiftspliktForSykestoenadsdelForArbeidstakereUtsendtFraUSAOgCanada"
                   )
                   }
                   .hvorMinstEnForekomst {(fastsatt(formuesverdiForFormuesandelForKjoeretoey.beloep) erStørreEnn 0) og (grunnlag(formuesverdiForFormuesandelForKjoeretoey.beloep) erLik 0)}

                val harOppgittFormueInnskudd = skattemelding
                   .forForekomsterAv{konto}
                   .filtrer {fastsatt(innskudd.trygdeEllerSkattemessigUnntak).finnesIkkeBlant(
                        "skattefriMedTrygdeavgiftspliktForHelsedel",
                        "skattefriMedTrygdeavgiftspliktForPensjonsdel",
                        "skattefriMedTrygdeavgiftsplikt",
                        "skattefriKildeskattPaaPensjon",
                        "skattefriUtenTrygdeavgiftsplikt",
                        "skattefriMedTrygdeavgiftspliktForSykestoenadsdelForArbeidstakereUtsendtFraUSAOgCanada"
                    )
                   }
                   .hvorMinstEnForekomst {(fastsatt(innskudd.beloep) erStørreEnn 0) og (grunnlag(innskudd.beloep) erLik 0)}

                val harOppgittFormueEiendomIUtlandet = skattemelding
                   .forForekomsterAv{fastEiendom}
                   .filtrer{(fastsatt(landkodeBeliggenhet) er oppgitt) og (fastsatt(landkodeBeliggenhet) erUlik "NO") }
                   .forForekomsterAv{fastEiendomSomFormuesobjekt}
                   .filtrer{fastsatt(formuesverdiForFormuesandel.trygdeEllerSkattemessigUnntak).finnesIkkeBlant(
                        "skattefriMedTrygdeavgiftspliktForHelsedel",
                        "skattefriMedTrygdeavgiftspliktForPensjonsdel",
                        "skattefriMedTrygdeavgiftsplikt",
                        "skattefriKildeskattPaaPensjon",
                        "skattefriUtenTrygdeavgiftsplikt",
                        "skattefriMedTrygdeavgiftspliktForSykestoenadsdelForArbeidstakereUtsendtFraUSAOgCanada"
                   )
                   }
                   .hvorMinstEnForekomst {(fastsatt(formuesverdiForFormuesandel.beloep) erStørreEnn 0) og (grunnlag(formuesverdiForFormuesandel.beloep) erLik 0)}

                 val harOppgittFormueEiendomUseksjonertBoenhetIUtlandet = skattemelding
                   .forForekomsterAv{fastEiendom}
                   .filtrer{(fastsatt(landkodeBeliggenhet) er oppgitt) og (fastsatt(landkodeBeliggenhet) erUlik "NO") }
                   .forForekomsterAv{fastEiendomSomFormuesobjekt}
                   .forForekomsterAv{useksjonertBoenhet}
                   .filtrer{fastsatt(formuesverdiForFormuesandel.trygdeEllerSkattemessigUnntak).finnesIkkeBlant(
                        "skattefriMedTrygdeavgiftspliktForHelsedel",
                        "skattefriMedTrygdeavgiftspliktForPensjonsdel",
                        "skattefriMedTrygdeavgiftsplikt",
                        "skattefriKildeskattPaaPensjon",
                        "skattefriUtenTrygdeavgiftsplikt",
                        "skattefriMedTrygdeavgiftspliktForSykestoenadsdelForArbeidstakereUtsendtFraUSAOgCanada"
                   )
                   }
                   .hvorMinstEnForekomst {(fastsatt(formuesverdiForFormuesandel.beloep) erStørreEnn 0) og (grunnlag(formuesverdiForFormuesandel.beloep) erLik 0)}

                hvis(harSaerskiltSkattepliktUtenrikstjenestemann og
                     uteITjenesteVedSluttenAvAaret og
                     (harOppgittFormueKjoeretoey eller harOppgittFormueInnskudd eller harOppgittFormueEiendomIUtlandet eller harOppgittFormueEiendomUseksjonertBoenhetIUtlandet)
