       val harUtflyttingsskattIFjor = historiskSkattemelding(EttÅrTilbake)
        { skattemelding ->
            skattemelding
          .hvorMinstEnForekomst {spesifikasjonAvUtflyttingOgUtflyttingsskatt er oppgitt}
        }

       val harUtflyttingsskattIForfjor = historiskSkattemelding(ToÅrTilbake)
        { skattemelding ->
            skattemelding
          .hvorMinstEnForekomst {spesifikasjonAvUtflyttingOgUtflyttingsskatt er oppgitt}
         }

         // Kontrollen gir utslag dersom skattyter har opprettet konrtet Utflyttingsskatt og
         //dato i dette kortet er 01.01 i inntektsåret
         // Skal gi utslag hvis årsakskode er AVB/EMI eller Svalbard
         // Skal gi utslag når fylt ut kortene om utflyttingsskatt i SME2023 eller SME2022
        skattemelding
           .fokusområde("Fokusområde")
           .forForekomsterAv{spesifikasjonAvUtflyttingOgUtflyttingsskatt}
           .utslagFor {

         val harDatoStartdatoIInntektsaaret =
             hvorMinstEnForekomst {
                (datoForUtflyttingsskatt erLik datoIInntektsaaret)
               }

         val harKortForUtflyttingsskattOgAarsak = hvorMinstEnForekomst{(land er oppgitt) og
                                                    ((aarsakTilUtflyttingsskatt erLik "bostedEtterSkatteavtale") eller
                                                     (aarsakTilUtflyttingsskatt erLik "skattemessigEmigrasjon"))}

         val harAarsakFlyttetTilSvalbard  = hvorMinstEnForekomst{(aarsakTilUtflyttingsskatt erLik "flyttetTilSvalbard")}

         hvis(harDatoStartdatoIInntektsaaret og
              harUtflyttingsskattIFjor og
              harUtflyttingsskattIForfjor og
             ((harKortForUtflyttingsskattOgAarsak) eller
              (harAarsakFlyttetTilSvalbard))
               )
             {
            betjeningsstrategiSettesTil(ikke, merknadStandard)
              }
             }
         }

