            .filtrer{startdatoForBosattIUtlandEtterSkatteavtale ikkeEr myndighetsfastsatt}
            .verdiFor{startdatoForBosattIUtlandEtterSkatteavtale}

        val startdatoEmigrert = skattemelding
            .forForekomsterAv {tilleggsinformasjonVedSkattemessigUtflytting}
            .filtrer{startdatoForKravOmOpphoerAvSkattepliktTilNorge ikkeEr myndighetsfastsatt}
            .verdiFor{startdatoForKravOmOpphoerAvSkattepliktTilNorge}

        // Kontrollen gir utslag dersom skattyter har opprettet konrtet Utflyttingsskatt og
        //dato i dette kortet er ulik
        //dato for skattemessig emigrasjon eller dato for bosted etter skatteavtale
        // Årsak = "Jeg har søkt eller fått innvilget krav om bosted etter skatteavtale med et annet land" eller
