       val latentGevinstFinansprodukt = aksjeOgAnnetFinansproduktOmfattetAvUtflyttingsskattForekomster
          .summer{latentGevinstVedRealisasjonAvAksjeOgAnnetFinansprodukt}

       val latentTapFinansprodukt = aksjeOgAnnetFinansproduktOmfattetAvUtflyttingsskattForekomster
          .summer{latentTapVedRealisasjonAvAksjeOgAnnetFinansprodukt}

       val andelISelskapMedDeltakerfastsettingOmfattetAvUtflyttingsskattForekomster = skattemelding
          .forForekomsterAv{andelISelskapMedDeltakerfastsettingOmfattetAvUtflyttingsskatt}

       val latentGevinstDeltakerfastsetting = andelISelskapMedDeltakerfastsettingOmfattetAvUtflyttingsskattForekomster
          .summer{latentGevinstVedRealisasjonAvAndel}

       val latentTapDeltakerfastsetting = andelISelskapMedDeltakerfastsettingOmfattetAvUtflyttingsskattForekomster
          .summer{latentTapVedRealisasjonAvAndel}

       val opsjonerIArbeidsforholdOmfattetAvUtflyttingsskattForekomster = skattemelding
          .forForekomsterAv{opsjonerIArbeidsforholdOmfattetAvUtflyttingsskatt}

       val latentGevinstOpsjonerIArbeidsforhold = opsjonerIArbeidsforholdOmfattetAvUtflyttingsskattForekomster
          .summer{latentGevinstVedRealisasjonAvOpsjon}

       val latentTapOpsjonerIArbeidsforhold = opsjonerIArbeidsforholdOmfattetAvUtflyttingsskattForekomster
          .summer{latentTapVedRealisasjonAvOpsjon}

       val fondskontoOmfattetAvUtflyttingsskattForekomster = skattemelding
          .forForekomsterAv{fondskontoOmfattetAvUtflyttingsskatt}

       val latentGevinstFondskonto = fondskontoOmfattetAvUtflyttingsskattForekomster
          .summer{(latentGevinstVedRealisasjonAvRentedel + latentGevinstVedRealisasjonAvAksjedel)}

       val latentTapFondskonto = fondskontoOmfattetAvUtflyttingsskattForekomster
          .summer{(latentTapVedRealisasjonAvRentedel + latentTapVedRealisasjonAvAksjedel)}

       val aksjesparekontoOmfattetAvUtflyttingsskattForekomster = skattemelding
          .forForekomsterAv{aksjesparekontoOmfattetAvUtflyttingsskatt}

       val latentGevinstAksjesparekonto= aksjesparekontoOmfattetAvUtflyttingsskattForekomster
          .summer{latentGevinstVedRealisasjonAvAksjesparekonto}

       val latentTapAksjesparekonto = aksjesparekontoOmfattetAvUtflyttingsskattForekomster
          .summer{latentTapVedRealisasjonAvAksjesparekonto}

       val sumNettoLatentGevinst = latentGevinstFinansprodukt - latentTapFinansprodukt +
                                  latentGevinstDeltakerfastsetting - latentTapDeltakerfastsetting +
                                  latentGevinstOpsjonerIArbeidsforhold - latentTapOpsjonerIArbeidsforhold +
                                  latentGevinstFondskonto - latentTapFondskonto +
                                  latentGevinstAksjesparekonto - latentTapAksjesparekonto

      val sumLatentGevinst = latentGevinstFinansprodukt + latentGevinstDeltakerfastsetting +
                             latentGevinstOpsjonerIArbeidsforhold + latentGevinstFondskonto +
                             latentGevinstAksjesparekonto

       val EOESLandkoder = aktiveLandkoder_KL
            .forForekomsterAv { kode }
            .filtrer { kodetillegg.medlemAvEoes erLik "Ja" }
            .verdierFor { tekniskNavn }

        // Kontrollen gir utslag dersom oppgitt latent gevinst på kortene under
        //    Aksjer og andre finansprodukter omfattet av utflyttingsskatt eller
        //    Andel i selskap med deltakerfastsetting omfattet av utflyttingsskatt eller
        //    Opsjoner i arbeidsforhold omfattet av utflyttingsskatt eller
        //    Aksjesparekonto omfattet av utflyttingsskatt eller
        //    Fondskonto omfattet av utflyttingsskatt OG
        // Gammel regel:
        //        Flytte/overføringsdato 1.1.2024 – 19.3.2024 og
        //        Netto latent gevinst over 500 000 (alle gevinster + alle tap) og
        //        Krevd betalingsutsettelse ELLER
        // Ny regel - overføring:
        //        Årsak utflyttingsskatt = overføring ved arv eller gave og
        //        Flytte/overføringsdato 20.3.2025 – 31.12.2025 og
        //        Netto latent gevinst over 100 000 (hvis innenfor EØS: alle gevinster + alle tap. Hvis utenfor EØS: alle gevinster uten å hensynta tap) og
        //        Krevd betalingsutsettelse (rater eller 12 år) ELLER
        // Ny regel - flytting
        //        Årsak utflyttingsskatt = "Jeg har søkt eller fått innvilget krav om bosted etter skatteavtale med et annet land" eller
        //                                 "Jeg har søkt eller fått innvilget skattemessig emigrasjon" eller
        //                                 "Jeg har flyttet til Svalbard" og
        //        Flytte/overføringsdato 20.3.2025 – 31.12.2025 og
        //        Netto latent gevinst over 3 000 000 (hvis innenfor EØS: alle gevinster + alle tap. Hvis utenfor EØS: alle gevinster uten å hensynta tap) og
        //        Krevd betalingsutsettelse (rater eller 12 år)
        skattemelding
         .fokusområde("Fokusområde")
         .forForekomsterAv{spesifikasjonAvUtflyttingOgUtflyttingsskatt}
         .utslagFor {

          val harKortForUtflyttingsskattOgAarsakArvEllerGave = hvorMinstEnForekomst{(land er oppgitt) og
                                                                     (aarsakTilUtflyttingsskatt erLik "arvetFraPersonBosattINorge")}

          val harKortForUtflyttingsskattOgAarsakAvtalebostedEllerEmigrasjon = hvorMinstEnForekomst{(land er oppgitt) og
                                                                     ((aarsakTilUtflyttingsskatt erLik "bostedEtterSkatteavtale") eller
                                                                      (aarsakTilUtflyttingsskatt erLik "skattemessigEmigrasjon"))}

          val harAarsakFlyttetTilSvalbard  = hvorMinstEnForekomst{(aarsakTilUtflyttingsskatt erLik "flyttetTilSvalbard")}

           val harUtflyttingsskattIkkeValgtIngenUtsettelseGammelRegel = hvorMinstEnForekomst {
             (datoForUtflyttingsskatt erEtterEllerLik startdatoIInntektsaaretGammelRegel) og
             (datoForUtflyttingsskatt erFør sluttdatoIInntektsaaretGammelRegel) og
             (betalingsmaateForUtflyttingsskatt erUlik "ingenUtsettelse") }

          val harUtflyttingsskattTilLandUtenforEOESIkkeValgtIngenUtsettelseNyRegel = hvorMinstEnForekomst {
             land.finnesIkkeBlant(EOESLandkoder) og
             (datoForUtflyttingsskatt erEtter startdatoIInntektsaaretNyRegel) og
             (datoForUtflyttingsskatt erFørEllerLik sluttdatoIInntektsaaretNyRegel) og
             (betalingsmaateForUtflyttingsskatt erUlik "ingenUtsettelse") }

          val harUtflyttingsskattTilLandInnenforEOESIkkeValgtIngenUtsettelseNyRegel = hvorMinstEnForekomst {
             land.finnesBlant(EOESLandkoder) og
             (datoForUtflyttingsskatt erEtter startdatoIInntektsaaretNyRegel) og
             (datoForUtflyttingsskatt erFørEllerLik sluttdatoIInntektsaaretNyRegel) og
             (betalingsmaateForUtflyttingsskatt erUlik "ingenUtsettelse") }

           val harUtflyttingsskattSvalbardIkkeValgtIngenUtsettelse = hvorMinstEnForekomst {
             (datoForUtflyttingsskatt erEtter startdatoIInntektsaaretNyRegel) og
             (datoForUtflyttingsskatt erFørEllerLik sluttdatoIInntektsaaretNyRegel) og
             (betalingsmaateForUtflyttingsskatt erUlik "ingenUtsettelse") }


         hvis((harUtflyttingsskattIkkeValgtIngenUtsettelseGammelRegel og
              (sumNettoLatentGevinst erStørreEnn 500_000)) eller
                 (harKortForUtflyttingsskattOgAarsakArvEllerGave og
                 ((harUtflyttingsskattTilLandUtenforEOESIkkeValgtIngenUtsettelseNyRegel og
                 (sumLatentGevinst erStørreEnn 100_000)) eller
                 (harUtflyttingsskattTilLandInnenforEOESIkkeValgtIngenUtsettelseNyRegel og
                 (sumNettoLatentGevinst erStørreEnn 100_000)))) eller
                     (harKortForUtflyttingsskattOgAarsakAvtalebostedEllerEmigrasjon og
                     ((harUtflyttingsskattTilLandUtenforEOESIkkeValgtIngenUtsettelseNyRegel og
                     (sumLatentGevinst erStørreEnn 3_000_000)) eller
                     (harUtflyttingsskattTilLandInnenforEOESIkkeValgtIngenUtsettelseNyRegel og
                     (sumNettoLatentGevinst erStørreEnn 3_000_000)))) eller
                           (harAarsakFlyttetTilSvalbard og
                            harUtflyttingsskattSvalbardIkkeValgtIngenUtsettelse og
                           (sumNettoLatentGevinst erStørreEnn 3_000_000))
             )

                {
                betjeningsstrategiSettesTil(ikke, merknadStandard)
                }
            }
        }

