sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {

        // Kontrollen gir utslag dersom realisert gevinst er høyere enn latent gevinst i minst et av disse kortene
        //    Aksjer og andre finansprodukter omfattet av utflyttingsskatt eller
        //    Andel i selskap med deltakerfastsetting omfattet av utflyttingsskatt eller
        //    Opsjoner i arbeidsforhold omfattet av utflyttingsskatt
        //    Aksjesparekonto omfattet av utflyttingsskatt
        //    Fondskonto omfattet av utflyttingsskatt
      skattemelding
            .utslagFor {

              val aksjeOgAnnetFinansproduktOmfattetAvUtflyttingsskattForekomster = forForekomsterAv{aksjeOgAnnetFinansproduktOmfattetAvUtflyttingsskatt}

              val latentGevinstFinansprodukt = aksjeOgAnnetFinansproduktOmfattetAvUtflyttingsskattForekomster
                 .summer{latentGevinstVedRealisasjonAvAksjeOgAnnetFinansprodukt}

              val latentTapFinansprodukt = aksjeOgAnnetFinansproduktOmfattetAvUtflyttingsskattForekomster
                 .summer{latentTapVedRealisasjonAvAksjeOgAnnetFinansprodukt}

              val realisertGevinsFinansprodukt = aksjeOgAnnetFinansproduktOmfattetAvUtflyttingsskattForekomster
                 .forForekomsterAv{realisasjonAvAksjeOgAnnetFinansprodukt}
                 .summer { skattepliktigGevinstVedRealisasjon.beloep }

              val realisertTapFinansprodukt = aksjeOgAnnetFinansproduktOmfattetAvUtflyttingsskattForekomster
                 .forForekomsterAv{realisasjonAvAksjeOgAnnetFinansprodukt}
                 .summer { skattepliktigTapVedRealisasjon.beloep }

              val realisertMinusLatentGevinstFinansprodukt = realisertGevinsFinansprodukt - latentGevinstFinansprodukt

              val realisertMinusLatentTapFinansprodukt = realisertTapFinansprodukt - latentTapFinansprodukt


              val andelISelskapMedDeltakerfastsettingOmfattetAvUtflyttingsskattForekomster = forForekomsterAv{andelISelskapMedDeltakerfastsettingOmfattetAvUtflyttingsskatt}

              val latentGevinstDeltakerfastsetting = andelISelskapMedDeltakerfastsettingOmfattetAvUtflyttingsskattForekomster
                 .summer{latentGevinstVedRealisasjonAvAndel}

              val latentTapDeltakerfastsetting = andelISelskapMedDeltakerfastsettingOmfattetAvUtflyttingsskattForekomster
                 .summer{latentTapVedRealisasjonAvAndel}

              val realisertGevinsDeltakerfastsetting = andelISelskapMedDeltakerfastsettingOmfattetAvUtflyttingsskattForekomster
                 .forForekomsterAv{realisasjonAvAndelISelskapMedDeltakerfastsetting}
                 .summer { skattepliktigGevinstVedRealisasjon.beloep }

              val realisertTapDeltakerfastsetting = andelISelskapMedDeltakerfastsettingOmfattetAvUtflyttingsskattForekomster
                 .forForekomsterAv{realisasjonAvAndelISelskapMedDeltakerfastsetting}
                 .summer { skattepliktigTapVedRealisasjon.beloep }

              val realisertMinusLatentGevinstDeltakerfastsetting = realisertGevinsDeltakerfastsetting - latentGevinstDeltakerfastsetting

              val realisertMinusLatentTapDeltakerfastsetting = realisertTapDeltakerfastsetting - latentTapDeltakerfastsetting


              val opsjonerIArbeidsforholdOmfattetAvUtflyttingsskattForekomster = forForekomsterAv{opsjonerIArbeidsforholdOmfattetAvUtflyttingsskatt}

              val latentGevinstOpsjonerIArbeidsforhold = opsjonerIArbeidsforholdOmfattetAvUtflyttingsskattForekomster
                 .summer{latentGevinstVedRealisasjonAvOpsjon}

              val latentTapOpsjonerIArbeidsforhold = opsjonerIArbeidsforholdOmfattetAvUtflyttingsskattForekomster
                 .summer{latentTapVedRealisasjonAvOpsjon}

              val realisertGevinsOpsjonerIArbeidsforhold = opsjonerIArbeidsforholdOmfattetAvUtflyttingsskattForekomster
                 .forForekomsterAv{realisasjonAvOpsjon}
                 .summer { skattepliktigGevinstVedRealisasjon.beloep }

              val realisertTapOpsjonerIArbeidsforhold = opsjonerIArbeidsforholdOmfattetAvUtflyttingsskattForekomster
                 .forForekomsterAv{realisasjonAvOpsjon}
                 .summer { skattepliktigTapVedRealisasjon.beloep }

              val realisertMinusLatentGevinstOpsjonerIArbeidsforhold = realisertGevinsOpsjonerIArbeidsforhold - latentGevinstOpsjonerIArbeidsforhold

              val realisertMinusLatentTapOpsjonerIArbeidsforhold = realisertTapOpsjonerIArbeidsforhold - latentTapOpsjonerIArbeidsforhold


              val fondskontoOmfattetAvUtflyttingsskattForekomster = forForekomsterAv{fondskontoOmfattetAvUtflyttingsskatt}

              val latentGevinstFondskonto = fondskontoOmfattetAvUtflyttingsskattForekomster
                 .summer{(latentGevinstVedRealisasjonAvRentedel + latentGevinstVedRealisasjonAvAksjedel)}

              val latentTapFondskonto = fondskontoOmfattetAvUtflyttingsskattForekomster
                 .summer{(latentTapVedRealisasjonAvRentedel + latentTapVedRealisasjonAvAksjedel)}

              val realisertGevinstFondskonto = fondskontoOmfattetAvUtflyttingsskattForekomster
                 .forForekomsterAv{realisasjonAvFondskonto}
                 .summer { skattepliktigGevinstVedRealisasjonAvAksjedel.beloep + skattepliktigGevinstVedRealisasjonAvRentedel.beloep }

              val realisertTapFondskonto = fondskontoOmfattetAvUtflyttingsskattForekomster
                 .forForekomsterAv{realisasjonAvFondskonto}
                 .summer { skattepliktigTapVedRealisasjonAvAksjedel.beloep + skattepliktigTapVedRealisasjonAvRentedel.beloep}

              val realisertMinusLatentGevinstFondskonto = realisertGevinstFondskonto - latentGevinstFondskonto

              val realisertMinusLatentTapFondskonto = realisertTapFondskonto - latentTapFondskonto


               val aksjesparekontoOmfattetAvUtflyttingsskattForekomster = forForekomsterAv{aksjesparekontoOmfattetAvUtflyttingsskatt}

               val latentGevinstAksjesparekonto= aksjesparekontoOmfattetAvUtflyttingsskattForekomster
                 .summer{latentGevinstVedRealisasjonAvAksjesparekonto}

              val latentTapAksjesparekonto = aksjesparekontoOmfattetAvUtflyttingsskattForekomster
                 .summer{latentTapVedRealisasjonAvAksjesparekonto}

              val realisertGevinstAksjesparekonto = aksjesparekontoOmfattetAvUtflyttingsskattForekomster
                 .forForekomsterAv{realisasjonAvAksjesparekonto}
                 .summer { skattepliktigGevinstVedRealisasjon.beloep }

              val realisertTapAksjesparekonto = aksjesparekontoOmfattetAvUtflyttingsskattForekomster
                 .forForekomsterAv{realisasjonAvAksjesparekonto}
                 .summer { skattepliktigTapVedRealisasjon.beloep}

              val realisertMinusLatentGevinstAksjesparekonto = realisertGevinstAksjesparekonto - latentGevinstAksjesparekonto

              val realisertMinusLatentTapAksjesparekonto = realisertTapAksjesparekonto - latentTapAksjesparekonto

             hvis((realisertMinusLatentGevinstFinansprodukt erStørreEnn 0) eller
                  (realisertMinusLatentTapFinansprodukt erStørreEnn 0) eller
                  (realisertMinusLatentGevinstDeltakerfastsetting erStørreEnn 0) eller
                  (realisertMinusLatentTapDeltakerfastsetting erStørreEnn 0) eller
                  (realisertMinusLatentGevinstOpsjonerIArbeidsforhold erStørreEnn 0) eller
                  (realisertMinusLatentTapOpsjonerIArbeidsforhold erStørreEnn 0) eller
                  (realisertMinusLatentGevinstFondskonto erStørreEnn 0) eller
                  (realisertMinusLatentTapFondskonto erStørreEnn 0) eller
                  (realisertMinusLatentGevinstAksjesparekonto erStørreEnn 0) eller
                  (realisertMinusLatentTapAksjesparekonto erStørreEnn 0)
                )
                {
                    betjeningsstrategiSettesTil(ikke, faktiskFeil)
                }
            }
        }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) 
    .importer(INNTEKTSAAR)  {
      ( D_Inntektsaar) ->

       val startdatoIInntektsaaretForFlytting = "$D_Inntektsaar-03-19".dato

       val harExitskattMedDatoForUtflyttingsskattEtterFristISisteFastsettingMedKlarsignal = skattemelding
         .forForekomsterAv{spesifikasjonAvUtflyttingOgUtflyttingsskatt}
         .hvorMinstEnForekomst{fastsattSisteKlarsignal(datoForUtflyttingsskatt) erEtter startdatoIInntektsaaretForFlytting}

        // Kontrollen gir utslag dersom skattyter har exit skatt i SISTE FASTSETTING SOM HAR FÅTT KLARSIGNAL/OPPGJØR (forekomst av Andre forhold -> Utflyttingsskatt -> SpesifikasjonAvUtflyttingOgUtflyttingsskatt)
        // OG flytte- eller overføringsdato er fra og med 20. mars i den fastsettingen
        // OG  ((det i denne fastsettingen finnes betalingsmåteForUtflyttingsskatt = ingenUtsettelse OG SP endrer verdien i dette feltet i ny fastsetting) 
        //     ELLER  (det i denne fastsettingen finnes betalingsmåteForUtflyttingsskatt lik aarligBetaling eller utsettelseAvHeleBeloepet OG SP endrer verdien i dette feltet i ny fastsetting til en verdi ULIK ingenUtsettelse))
      skattemelding
         .fokusområde("Fokusområde")
         .forForekomsterAv{spesifikasjonAvUtflyttingOgUtflyttingsskatt}
         .utslagFor {

               val harEndretBetalingsmaateForutflyttingsskattFraIngenUtsettelse = hvorMinstEnForekomst {(fastsattSisteKlarsignal(betalingsmaateForUtflyttingsskatt) erLik "ingenUtsettelse") og
                (fastsatt(betalingsmaateForUtflyttingsskatt) erUlik "ingenUtsettelse")}

               val harEndretBetalingsmaateForutflyttingsskattFraAarligBetalingEllerUtsettelseAvHeleBeloepetTilNoeAnnetEnnIngenUtsettelse = 
                  hvorMinstEnForekomst {(fastsattSisteKlarsignal(betalingsmaateForUtflyttingsskatt).finnesBlant("aarligBetaling", "utsettelseAvHeleBeloepet")) og
                                        (fastsatt(betalingsmaateForUtflyttingsskatt).finnesIkkeBlant("ingenUtsettelse", "aarligBetaling", "utsettelseAvHeleBeloepet"))}



             hvis(harExitskattMedDatoForUtflyttingsskattEtterFristISisteFastsettingMedKlarsignal og
                  (harEndretBetalingsmaateForutflyttingsskattFraIngenUtsettelse eller harEndretBetalingsmaateForutflyttingsskattFraAarligBetalingEllerUtsettelseAvHeleBeloepetTilNoeAnnetEnnIngenUtsettelse)                  
                )
                {
                    betjeningsstrategiSettesTil(ikke, faktiskFeil)
                }
            }
        }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
      val erSkattepliktigTilSvalbard = skattepliktGjeldende
           .forForekomsterAv { skatteplikt }
           .hvorMinstEnForekomst{((personligSkattesubjekt.skattepliktTilSvalbard erUlik "ingenSkatteplikt") og (personligSkattesubjekt.skattepliktTilSvalbard er oppgitt)) eller
                                ((doedsbo.skattepliktTilSvalbard erUlik "ingenSkatteplikt") og (doedsbo.skattepliktTilSvalbard er oppgitt))}

       // Utslag hvis skattyter har fylt ut kortet «Utflyttingsskatt»
       // det er angitt årsak «Jeg har flyttet til Svalbard»
       // skattyter ikke er skattemessig bosatt på Svalbard i inntektsåret
        utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .fokusområde("Fokusområde")
            .forForekomsterAv(spesifikasjonAvUtflyttingOgUtflyttingsskatt)
            .utslagFor {
                val harUtflyttingsskatt = skattemeldingUtgått
                    .hvorMinstEnForekomst { spesifikasjonAvUtflyttingOgUtflyttingsskatt er oppgitt }

                val endringsaarsakFlyttetTilSvalbard =
                    hvorMinstEnForekomst {
                        aarsakTilUtflyttingsskatt.finnesBlant(
                            "flyttetTilSvalbard"
                        )
                    }

                hvis(
                    ikke(erSkattepliktigTilSvalbard) og
                        harUtflyttingsskatt og endringsaarsakFlyttetTilSvalbard
                )
                {
                    betjeningsstrategiSettesTil(ikke, merknadStandard)
                }
            }
        }
    }

