sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        val harUtflyttingsskatt = utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .hvorMinstEnForekomst { spesifikasjonAvUtflyttingOgUtflyttingsskatt er oppgitt }
        }

        // Det finnes en forekomst av enten
        // AndreForhold -> Utflyttingsskatt -> AksjeOgAnnetFinansproduktOmfattetAvUtflyttingsskatt eller
        // AndreForhold -> Utflyttingsskatt -> OpsjonerIArbeidsforholdOmfattetAvUtflyttingsskatt eller
        // AndreForhold -> Utflyttingsskatt -> AndelISelskapMedDeltakerfastsettingOmfattetAvUtflyttingsskatt
        // OG det ikke finnes en forekomst av Utflyttingsskatt -> SpesifikasjonAvUtflyttingOgUtflyttingsskatt
        utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .fokusområde("Fokusområde")
            .forForekomsterAv(
                gruppe(
                    aksjeOgAnnetFinansproduktOmfattetAvUtflyttingsskatt,
                    andelISelskapMedDeltakerfastsettingOmfattetAvUtflyttingsskatt,
                    opsjonerIArbeidsforholdOmfattetAvUtflyttingsskatt
                )
            )
            .utslagFor {
                val harForekomst = hvorMinstEnForekomst { selskapetsNavn er oppgitt }


                hvis(
                    harForekomst og
                        ikke(harUtflyttingsskatt)
                )
                {
                    betjeningsstrategiSettesTil(ikke, merknadStandard, skjulerSkatteberegning)
                }
            }
        }
    }

