    .kontekst(ValideringSkattepliktig) {

        // Kontrollen gir utslag dersom oppgitt beløp i realisert gevinst/tap er større enn latent gevinst/tap på kortet
        // Aksjer og andre finansprodukter omfattet av utflyttingsskatt eller
        // Andel i selskap med deltakerfastsetting omfattet av utflyttingsskatt eller
        // Opsjoner i arbeidsforhold omfattet av utflyttingsskatt
        //gir bare utslag dersom latent gevint/tap er oppgitt, for ikke å få flere merknader samtidig
        skattemelding
            .forForekomsterAv{
            grupper(
                aksjeOgAnnetFinansproduktOmfattetAvUtflyttingsskatt,
                andelISelskapMedDeltakerfastsettingOmfattetAvUtflyttingsskatt,
                opsjonerIArbeidsforholdOmfattetAvUtflyttingsskatt
            )
            }
            .utslagFor {

                val aksjeOgAnnetFinansproduktOmfattetAvUtflyttingsskattForekomster = skattemelding
                    .forForekomsterAv{aksjeOgAnnetFinansproduktOmfattetAvUtflyttingsskatt}

                val realisertGevinsFinansprodukt = aksjeOgAnnetFinansproduktOmfattetAvUtflyttingsskattForekomster
                    .forForekomsterAv{realisasjonAvAksjeOgAnnetFinansprodukt}
                    .summer { skattepliktigGevinstVedRealisasjon.beloep }

                val realisertTapFinansprodukt = aksjeOgAnnetFinansproduktOmfattetAvUtflyttingsskattForekomster
                    .forForekomsterAv{realisasjonAvAksjeOgAnnetFinansprodukt}
                    .summer { skattepliktigTapVedRealisasjon.beloep }

                val latentGevinsFinansprodukt = aksjeOgAnnetFinansproduktOmfattetAvUtflyttingsskattForekomster
                    .summer { latentGevinstVedRealisasjonAvAksjeOgAnnetFinansprodukt }

                val latentTapFinansprodukt = aksjeOgAnnetFinansproduktOmfattetAvUtflyttingsskattForekomster
                    .summer { latentTapVedRealisasjonAvAksjeOgAnnetFinansprodukt }

                val andelISelskapMedDeltakerfastsettingOmfattetAvUtflyttingsskattForekomster = skattemelding
                   .forForekomsterAv{andelISelskapMedDeltakerfastsettingOmfattetAvUtflyttingsskatt}

                val realisertGevinsDeltakerfastsetting = andelISelskapMedDeltakerfastsettingOmfattetAvUtflyttingsskattForekomster
                   .forForekomsterAv{realisasjonAvAndelISelskapMedDeltakerfastsetting}
                   .summer { skattepliktigGevinstVedRealisasjon.beloep }

                val realisertTapDeltakerfastsetting = andelISelskapMedDeltakerfastsettingOmfattetAvUtflyttingsskattForekomster
                   .forForekomsterAv{realisasjonAvAndelISelskapMedDeltakerfastsetting}
                   .summer { skattepliktigTapVedRealisasjon.beloep }

                val latentGevinsDeltakerfastsetting = andelISelskapMedDeltakerfastsettingOmfattetAvUtflyttingsskattForekomster
                   .summer { latentGevinstVedRealisasjonAvAndel }

                val latentTapDeltakerfastsetting = andelISelskapMedDeltakerfastsettingOmfattetAvUtflyttingsskattForekomster
                   .summer { latentTapVedRealisasjonAvAndel }

                val opsjonerIArbeidsforholdOmfattetAvUtflyttingsskattForekomster = skattemelding
                   .forForekomsterAv{opsjonerIArbeidsforholdOmfattetAvUtflyttingsskatt}

                val realisertGevinsOpsjonerIArbeidsforhold = opsjonerIArbeidsforholdOmfattetAvUtflyttingsskattForekomster
                   .forForekomsterAv{realisasjonAvOpsjon}
                   .summer { skattepliktigGevinstVedRealisasjon.beloep }

                val realisertTapOpsjonerIArbeidsforhold = opsjonerIArbeidsforholdOmfattetAvUtflyttingsskattForekomster
                   .forForekomsterAv{realisasjonAvOpsjon}
                   .summer { skattepliktigTapVedRealisasjon.beloep }

                val latentGevinsOpsjonerIArbeidsforhold = opsjonerIArbeidsforholdOmfattetAvUtflyttingsskattForekomster
                   .summer { latentGevinstVedRealisasjonAvOpsjon }

                val latentTapOpsjonerIArbeidsforhold = opsjonerIArbeidsforholdOmfattetAvUtflyttingsskattForekomster
                   .summer { latentTapVedRealisasjonAvOpsjon }


                hvis(
                    ((latentGevinsFinansprodukt erStørreEnn 0) og (realisertGevinsFinansprodukt erStørreEnn latentGevinsFinansprodukt)) eller
                        ((latentTapFinansprodukt erStørreEnn 0) og (realisertTapFinansprodukt erStørreEnn latentTapFinansprodukt)) eller
                        ((latentGevinsDeltakerfastsetting erStørreEnn 0) og (realisertGevinsDeltakerfastsetting erStørreEnn latentGevinsDeltakerfastsetting)) eller
                        ((latentTapDeltakerfastsetting erStørreEnn 0) og (realisertTapDeltakerfastsetting erStørreEnn latentTapDeltakerfastsetting)) eller
                        ((latentGevinsOpsjonerIArbeidsforhold erStørreEnn 0) og (realisertGevinsOpsjonerIArbeidsforhold erStørreEnn latentGevinsOpsjonerIArbeidsforhold)) eller
                        ((latentTapOpsjonerIArbeidsforhold erStørreEnn 0) og (realisertTapOpsjonerIArbeidsforhold erStørreEnn latentTapOpsjonerIArbeidsforhold))
                )
                {
                    betjeningsstrategiSettesTil(ikke, merknadStandard)
                }
            }
        }

