       val latentGevinstOpsjonerIArbeidsforhold = opsjonerIArbeidsforholdOmfattetAvUtflyttingsskattForekomster
          .summer{latentGevinstVedRealisasjonAvOpsjon}

       val latentTapOpsjonerIArbeidsforhold = opsjonerIArbeidsforholdOmfattetAvUtflyttingsskattForekomster
          .summer{latentTapVedRealisasjonAvOpsjon}

       val fondskontoOmfattetAvUtflyttingsskattForekomster = skattemelding
          .forForekomsterAv{fondskontoOmfattetAvUtflyttingsskatt}

       val latentGevinstFondskonto = fondskontoOmfattetAvUtflyttingsskattForekomster
          .summer{(latentGevinstVedRealisasjonAvRentedel + latentGevinstVedRealisasjonAvAksjedel)}

       val latentTapFondskonto = fondskontoOmfattetAvUtflyttingsskattForekomster
          .summer{(latentTapVedRealisasjonAvRentedel + latentTapVedRealisasjonAvAksjedel)}

       val aksjesparekontoOmfattetAvUtflyttingsskattForekomster = skattemelding
          .forForekomsterAv{aksjesparekontoOmfattetAvUtflyttingsskatt}

       val latentGevinstAksjesparekonto= aksjesparekontoOmfattetAvUtflyttingsskattForekomster
          .summer{latentGevinstVedRealisasjonAvAksjesparekonto}

       val latentTapAksjesparekonto = aksjesparekontoOmfattetAvUtflyttingsskattForekomster
          .summer{latentTapVedRealisasjonAvAksjesparekonto}

       val sumNettoLatentGevinst = latentGevinstFinansprodukt - latentTapFinansprodukt +
                                  latentGevinstDeltakerfastsetting - latentTapDeltakerfastsetting +
                                  latentGevinstOpsjonerIArbeidsforhold - latentTapOpsjonerIArbeidsforhold +
                                  latentGevinstFondskonto - latentTapFondskonto +
                                  latentGevinstAksjesparekonto - latentTapAksjesparekonto

       val sumNettoLatentTap = latentTapFinansprodukt - latentGevinstFinansprodukt +
                               latentTapDeltakerfastsetting - latentGevinstDeltakerfastsetting +
                               latentTapOpsjonerIArbeidsforhold - latentGevinstOpsjonerIArbeidsforhold +
                               latentTapFondskonto - latentGevinstFondskonto +
                               latentTapAksjesparekonto - latentGevinstAksjesparekonto

      val sumRealisertTapSelskapMedDeltakerfastsetting = skattemelding
            .forForekomsterAv {andelISelskapMedDeltakerfastsettingOmfattetAvUtflyttingsskatt}
            .forForekomsterAv{realisasjonAvAndelISelskapMedDeltakerfastsetting}
            .summer { skattepliktigTapVedRealisasjon.beloep }

       val EOESLandkoder = aktiveLandkoder_KL
            .forForekomsterAv { kode }
            .filtrer { kodetillegg.medlemAvEoes erLik "Ja" }
            .verdierFor { tekniskNavn }

        //  Kontrollen gir utslag dersom samlet netto latent tap eller gevinst på kortene under tilsammen er større enn 500 000
        //    Aksjer og andre finansprodukter omfattet av utflyttingsskatt eller
        //    Andel i selskap med deltakerfastsetting omfattet av utflyttingsskatt eller
        //    Opsjoner i arbeidsforhold omfattet av utflyttingsskatt
        //    Aksjesparekonto omfattet av utflyttingsskatt
        //    Fondskonto omfattet av utflyttingsskatt
        // Og Oppgitt realisert tap på kortet "Andel i selskap med deltakerfastsetting omfattet av utflyttingsskatt"(kort i kort Realisert gevinst/tap)
        // Gir bare utslag dersom oppgitt land i kortet "Utflyttingsskatt" er Svalbard eller et EØS-land
        // og Dato for utflyttingsskatt er etter 1.1 og før 20.03 i inntektsåret
        // og har krevd betalingsutsettelse
        skattemelding
            .fokusområde("Fokusområde")
            .forForekomsterAv{spesifikasjonAvUtflyttingOgUtflyttingsskatt}
            .utslagFor {

              val harKortForUtflyttingsskatt = hvorMinstEnForekomst { land er oppgitt }

              val harUtflyttingsskattTilLandInnenforEOESIkkeValgtIngenUtsettelse = hvorMinstEnForekomst {
                    land.finnesBlant(EOESLandkoder) og
                    (datoForUtflyttingsskatt erEtter startdatoIInntektsaaret) og
                    (datoForUtflyttingsskatt erFør sluttdatoIInntektsaaret) og
                    (betalingsmaateForUtflyttingsskatt erUlik "ingenUtsettelse") }

             val harUtflyttingsskattTilSvalbard  = hvorMinstEnForekomst{(aarsakTilUtflyttingsskatt erLik "flyttetTilSvalbard")}

             val harUtflyttingsskattSvalbardIkkeValgtIngenUtsettelse = hvorMinstEnForekomst {
                    (datoForUtflyttingsskatt erEtter startdatoIInntektsaaret) og
                    (datoForUtflyttingsskatt erFør sluttdatoIInntektsaaret) og
                    (betalingsmaateForUtflyttingsskatt erUlik "ingenUtsettelse") }

             hvis(((harKortForUtflyttingsskatt og
                  harUtflyttingsskattTilLandInnenforEOESIkkeValgtIngenUtsettelse) eller
                   (harUtflyttingsskattTilSvalbard og
                   harUtflyttingsskattSvalbardIkkeValgtIngenUtsettelse)) og
                  ((sumNettoLatentGevinst erStørreEnn 500_000) eller
                   (sumNettoLatentTap erStørreEnn 500_000)) og
                  (sumRealisertTapSelskapMedDeltakerfastsetting erStørreEnn 0)
                )
                {
                    betjeningsstrategiSettesTil(ikke, merknadStandard)
                }
            }
        }

