sakstype(Personlig)
    .kontekst(ValideringSkattepliktig)
    {
        val erGlobalskattepliktigTilSvalbardIInntektsaaret = skattepliktGjeldende
            .forForekomsterAv { skatteplikt }
            .hvorMinstEnForekomst{personligSkattesubjekt.skattepliktTilSvalbard erLik "global"}

        val haddeGlobalskattepliktigTilSvalbardForrigeAar = fjoraaretsSkattepliktGjeldende
            .forForekomsterAv { skatteplikt }
            .hvorMinstEnForekomst{ personligSkattesubjekt.skattepliktTilSvalbard erLik "global" }

        val haddeGlobalskattepliktTilNorgeForrigeAar = fjoraaretsSkattepliktGjeldende
            .forForekomsterAv { skatteplikt }
            .hvorMinstEnForekomst{ personligSkattesubjekt.skattepliktTilNorge erLik "global" }

        // Merknad til SP som har flyttet til Svalbard i inntektsåret
        // gis informasjon om utflyttingsskatt for latent gevinst over kr 500 000
        skattemelding
            .utslagFor {


                hvis(erGlobalskattepliktigTilSvalbardIInntektsaaret og
                    haddeGlobalskattepliktTilNorgeForrigeAar og
                    ikke(haddeGlobalskattepliktigTilSvalbardForrigeAar)
                )
                {
                    betjeningsstrategiSettesTil(ikke, merknadStandard)
                }
            }
    }

sakstype(Personlig)
      .kontekst(ValideringSkattepliktig)
      .importer(STARTDATO_I_INNTEKTSAARET) {
         (D_startdatoIInntektsaaret) ->

         val startdatoIInntektsaaret = D_startdatoIInntektsaaret

         val harUtslagPaaAnnenKontroll = utslagPåKontroll("EMIGRASJON_AVTALEBOSTED_SAMME_DATO")

         val harGlobalSkattepliktIInntektsaaret = skattepliktGjeldende
           .forForekomsterAv { skatteplikt }
           .hvorMinstEnForekomst { personligSkattesubjekt.skattepliktTilNorge erLik "global" }

         val harGlobalSkattepliktFjoraaret = fjoraaretsSkattepliktGjeldende
           .forForekomsterAv { skatteplikt }
           .hvorMinstEnForekomst { personligSkattesubjekt.skattepliktTilNorge erLik "global" }

         val harBegrensetSkattepliktIInntektsaaret = skattepliktGjeldende
           .forForekomsterAv { skatteplikt }
           .hvorMinstEnForekomst { personligSkattesubjekt.skattepliktTilNorge erLik "begrenset" }

         val harBegrensetSkattepliktFjoraaret = fjoraaretsSkattepliktGjeldende
           .forForekomsterAv { skatteplikt }
           .hvorMinstEnForekomst { personligSkattesubjekt.skattepliktTilNorge erLik "begrenset" }

         val harSaerskiltSkattepliktIInntektsaaret = skattepliktGjeldende
           .forForekomsterAv { skatteplikt }
           .forForekomsterAv { personligSkattesubjekt.saerskiltSkatteplikt }
           .hvorMinstEnForekomst {
                  type.finnesBlant(
                      "skattepliktPaaLoennFraDenNorskeStatOpptjentIUtlandet",
                      "skattepliktEtterPetroleumsskatteloven",
                      "skattepliktSomSjoemann",
                      "skattepliktAvNaeringsdriftEiendomMv",
                      "kildeskattepliktPaaLoenn",
                      "kildeskattepliktPaaPensjon",
                      "skattepliktVedUtenriksoppholdINorskStatstjenesteEllerNato"
                  )
              }

         val harSaerskiltSkattepliktIFjor = fjoraaretsSkattepliktGjeldende
           .forForekomsterAv { skatteplikt }
           .forForekomsterAv { personligSkattesubjekt.saerskiltSkatteplikt }
           .hvorMinstEnForekomst {
                type.finnesBlant(
                      "skattepliktPaaLoennFraDenNorskeStatOpptjentIUtlandet",
                      "skattepliktEtterPetroleumsskatteloven",
                      "skattepliktSomUtenrikstjenestemann",
                      "skattepliktSomSjoemann",
                      "skattepliktAvNaeringsdriftEiendomMv",
                      "kildeskattepliktPaaLoenn",
                      "kildeskattepliktPaaPensjon",
                      "skattepliktVedUtenriksoppholdINorskStatstjenesteEllerNato"
                  )
              }

           val harUtflyttingsskattIFjor = fjoraaretsSkattemelding
             .hvorMinstEnForekomst {spesifikasjonAvUtflyttingOgUtflyttingsskatt er oppgitt}

            val harKravOmBostedEtterSkatteavtaleIAnnetLandIFjor = fjoraaretsSkattemelding
              .hvorMinstEnForekomst {tilleggsinformasjonVedSkattemessigBosattIUtlandEtterSkatteavtale er oppgitt}

            val harKravOmSkattemessigEmigrasjonIFjor = fjoraaretsSkattemelding
              .hvorMinstEnForekomst {tilleggsinformasjonVedSkattemessigUtflytting er oppgitt}

          //Skattepiktig har fylt ut kortet "Krav om skattemessig emigrasjon" og
          // flyttedato i dette kortet er 01.01. i inntektsåret
          // og skattyter har global skatteplikt i inntektsåret og fjoråret
          //Skal ikke gi utslag på skattepliktig som har begrenset skatteplikt eller særskatteplikt i året før inntektsåret
          // og som fortsatt har slik skatteplikt i inntektsåret
          //Skal ikke gi utslag på skattepliktig som i året før inntektsåret har fylt ut ett eller flere av følgende kort: "Utflyttingsskatt",
          //     "Krav om bosted etter skatteavtale i annet land" eller "Krav om skattemessig emigrasjon"
          //skal ikke gi utslag hvis det er utslag på faktisk feil EMIGRASJON_AVTALEBOSTED_SAMME_DATO
          skattemelding
              .fokusområde("Fokusområde")
              .forForekomsterAv { tilleggsinformasjonVedSkattemessigUtflytting }
              .utslagFor {
                val startdatoOppgittOgEtterDatoIInntektsaar = hvorMinstEnForekomst {
                  (startdatoForKravOmOpphoerAvSkattepliktTilNorge er oppgitt) og
                	(startdatoForKravOmOpphoerAvSkattepliktTilNorge erLik startdatoIInntektsaaret)
             }
             hvis(ikke(harUtslagPaaAnnenKontroll) og
                  harGlobalSkattepliktIInntektsaaret og harGlobalSkattepliktFjoraaret og
                  ikke(harBegrensetSkattepliktIInntektsaaret) og ikke(harBegrensetSkattepliktFjoraaret) og
                  ikke(harSaerskiltSkattepliktIInntektsaaret) og ikke(harSaerskiltSkattepliktIFjor) og
                  ikke(harUtflyttingsskattIFjor) og ikke(harKravOmBostedEtterSkatteavtaleIAnnetLandIFjor) og
                  ikke(harKravOmSkattemessigEmigrasjonIFjor) og
                  startdatoOppgittOgEtterDatoIInntektsaar)
            {
               betjeningsstrategiSettesTil(ikke, merknadStandard)
          }
        }
    }

