             .forForekomsterAv{fastEiendom}
             .filtrer{eiendomsforekomster ->
                 eiendomsforekomster
                   .forForekomsterAv{fastEiendomSomFormuesobjekt}
                   .hvorMinstEnForekomst{grunnlag(eiendomstype).finnesBlant("ikkeUtleidNaeringseiendomINorge", "naeringseiendom")}
             }
             .verdierFor{grunnlag(sergEiendomsidentifikator)}

          val harUVAvGittType = utvidetVeiledning
            .forForekomsterAv { utvidetveiledning }
            .forForekomsterAv { metadata }
            .hvorMinstEnForekomst { type erLik "NAERINGSEIENDOM" }


        // Kontrollen gir utslag dersom skattyter har fått produsert utvidet veiledning av en gitt type
        // skal bare vises UV dersom næringseiendommen i UV IKKE finnes i Grunnlag
        // brukes for å styre om den skal vises eller ikke
        utvidetVeiledning
            .forForekomsterAv { utvidetveiledning }
            .forForekomsterAv{fastEiendom}
            .utslagFor {

                val harNaeringseiendomIUVSomIkkeLiggerIGrunnlag = hvorMinstEnForekomst {fastsatt(sergEiendomsidentifikator).finnesIkkeBlant(naeringseiendommeriGrunnlag)}

                hvis(harUVAvGittType og
                    harNaeringseiendomIUVSomIkkeLiggerIGrunnlag)
                {
                    betjeningsstrategiSettesTil(
                        ikke, visUtvidetVeiledning
                    )
                }
            }
    }

