         val harUtleidFjoraaretSjablongbehandlet = fjoraaretsSkattemelding
             .forForekomsterAv{
                 grupper(
                     sjablongbehandletUtleie,
                     sjablongOgRegnskapsbehandletUtleie)}
             .hvorMinstEnForekomst {fastsatt(bruttoLeieinntektVedSjablongbehandletUtleie.beloep) erStørreEnn 0}

         val harUtleidFjoraaretRegnskapsbehandlet = fjoraaretsSkattemelding
             .forForekomsterAv{
                 grupper(
                     regnskapsbehandletUtleie,
                     sjablongOgRegnskapsbehandletUtleie)}
             .hvorMinstEnForekomst {fastsatt(bruttoLeieinntektVedRegnskapsbehandletUtleie.beloep) erStørreEnn 0}


        // Kontrollen gir utslag dersom skattyter IKKE hadde utleie i fjor, og ikke har oppgitt noen utleie i år,
        // Skal gi utslag hvis det er produsert UV av type "Utleie sekundærbolig på "skattyter
         skattemelding
             .utslagFor {

             val harUtleieIAar = forForekomsterAv{fastEiendom}
                  .forForekomsterAv{fastEiendomSomUtleieobjekt}
                 .hvorMinstEnForekomst {(fastsatt(bruttoLeieinntektVedSjablongbehandletUtleie.beloep) erStørreEnn 0) eller
