                                        (fastsatt(underskuddForAndel.fradragsberettigetBeloep.beloep) erStørreEnn 0) eller
                                        (fastsatt(andelAvNettoLeieinntektEllerUnderskudd) erStørreEnn 0)}

             hvis(harUtvidetVeiledningPaaUtleie og
                  ikke(harUtleieIAar) og
                  ikke(harUtleidFjoraaretSjablongbehandlet) og
                  ikke(harUtleidFjoraaretRegnskapsbehandlet)
             )
             {
                 betjeningsstrategiSettesTil(ikke, visUtvidetVeiledning, skjulerSkatteberegning)
             }
         }
    }

