sakstype(Personlig)
        .kontekst(ValideringSkattepliktig) {

          val harUtslagPaaKontroll =
         utslagPåKontroll("VALUTAREGISTER_KRYPTO_KJENT_FORHANDLER_MERKNAD_TOPP") eller
         utslagPåKontroll("VALUTAREGISTER_KRYPTO_KJENT_FORHANDLER_MERKNAD_TIPS") eller
         utslagPåKontroll("VALUTAREGISTER_KRYPTO_KJENT_FORHANDLER_IKKE_MERKNAD") eller
         utslagPåKontroll("VALUTAREGISTER_KRYPTO_MERKNAD_TIPS") eller
         utslagPåKontroll("VALUTAREGISTER_KRYPTO_MERKNAD_TOPP") eller
         utslagPåKontroll("VALUTAREGISTER_KRYPTO_IKKE_MERKNAD") eller
         utslagPåKontroll("VALUTAREGISTER_KRYPTO_KJENT_FORHANDLER_MILD_MERKNAD_TOPP") eller
         utslagPåKontroll("VALUTAREGISTER_KRYPTO_KJENT_FORHANDLER_MILD_MERKNAD_TIPS") eller
         utslagPåKontroll("VALUTAREGISTER_KRYPTO_KJENT_FORHANDLER_MILD_IKKE_MERKNAD") eller
         utslagPåKontroll("KRYPTO_OPPLYSNINGER_FIRI_TIPS") eller
         utslagPåKontroll("KRYPTO_OPPLYSNINGER_FIRI_IKKE_MERKNAD")


          val navnSomHarFormuesverdiVirtuellValutaIFjor = fjoraaretsSkattemelding
             .forForekomsterAv { virtuellEiendel }
             .filtrer{fastsatt(formuesverdiforVirtuellEiendel.beloep) erStørreEnn 0}
             .verdierFor{fastsatt(navn)}

          val antallEgenfastsatteVirtuelleEiendeler = skattemelding
             .forForekomsterAv{virtuellEiendel}
             .filtrer{(fastsatt(formuesverdiforVirtuellEiendel.beloep) erStørreEnn 0) eller
                      (fastsatt(skattepliktigGevinstVedRealisasjonAvVirtuellEiendel.beloep) erStørreEnn 0) eller
                      (fastsatt(skattepliktigTapVedRealisasjonAvVirtuellEiendel.fradragsberettigetBeloep.beloep) erStørreEnn 0) eller
                      (fastsatt(annenSkattepliktigKapitalinntektFraVirtuellEiendel.beloep) erStørreEnn 0)}
             .antall

        // Kontrollen gir utslag dersom skattyter har fått produsert utvidet veiledning av en gitt type
        // skal bare gi utslag dersom det fantes formuesverdi større enn 0 for forrige år
        // skal Ikke gi utslag hvis det er egenfastsatt mer enn et kort med virtuelle eiendeler
        // brukes for å styre om den skal vises eller ikke
        // skal ikke gi utslag hvis det er utslag på andre kryptokontroller
        utvidetVeiledning
            .forForekomsterAv { utvidetveiledning }
            .utslagFor {
                val harFormueIFjor= forForekomsterAv{virtuellEiendel}
                   .hvorMinstEnForekomst{fastsatt(navn).finnesBlant(navnSomHarFormuesverdiVirtuellValutaIFjor)}

                val harUVAvGittType = forForekomsterAv { metadata }
                      .hvorMinstEnForekomst { type erLik "VIRTUELL_EIENDEL"}

                hvis(harFormueIFjor og
                     harUVAvGittType og
                     (antallEgenfastsatteVirtuelleEiendeler erMindreEnn 2) og
                    ikke(harUtslagPaaKontroll))
                {
                    betjeningsstrategiSettesTil(
                        ikke, visUtvidetVeiledning
                    )
                }
            }
    }

