sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        // kontrollen gir utslag dersom skattyter har redusert renteinntekt på verdipapirfond eller obligasjon
        // gir bare utslag dersom det ikke er oppgitt endringsårsak = "Dette er overført til eller fra ektefelle, samboer eller andre"
        // kontrollen gir merknad i SME
        skattemelding
            .fokusområde("Fokusområde")
            //.forForekomsterAv{grupper(verdipapirfond, obligasjonOgSertifikat)}
            .utslagFor {

                val differanseRenteinntektVerdipapirfond = skattemelding
                  .forForekomsterAv{verdipapirfond}
                  .filtrer { verdipapirForekomst ->
                     (
                        (aarsakTilEndring ikkeEr oppgitt) eller
                        verdipapirForekomst
                          .forForekomsterAv{aarsakTilEndring}
                          .hvorMinstEnForekomst { fastsatt(aarsak) erUlik "overfortTilFraEktefelle" } )
                  }
                  .hvorMinstEnForekomst{(grunnlag(skattepliktigRenteinntekt.beloep) - fastsatt(skattepliktigRenteinntekt.beloep)) erStørreEnn 50_000 }

                val differanseRenteinntektObligasjon = skattemelding
                   .forForekomsterAv{obligasjonOgSertifikat}
                   .filtrer { obligasjonsForekomst ->
                     (
                        (aarsakTilEndring ikkeEr oppgitt) eller
                        obligasjonsForekomst
                          .forForekomsterAv{aarsakTilEndring}
                          .hvorMinstEnForekomst { fastsatt(aarsak) erUlik "overfortTilFraEktefelle" } )
                  }
                   .hvorMinstEnForekomst{ (grunnlag(renteinntektAvObligasjonOgSertifikat.beloep) - fastsatt(renteinntektAvObligasjonOgSertifikat.beloep)) erStørreEnn 50_000 }

                hvis(ikke(harInnsending) og
                     (differanseRenteinntektVerdipapirfond eller differanseRenteinntektObligasjon)) {
                    betjeningsstrategiSettesTil(
                        ikke, merknadInformasjon
                    )
                }
            }
        }

